import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";

const STORAGE_KEY = "dk_kyc_step1_docType";

export default function KYCStep1() {
  const [docType, setDocType] = useState("Aadhaar Card");
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) setDocType(saved);
  }, []);

  const handleNext = (e) => {
    e?.preventDefault();
    localStorage.setItem(STORAGE_KEY, docType);
    navigate("/kyc-step2");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form
        onSubmit={handleNext}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-xl border border-gray-100"
      >
        <div className="text-center mb-6">
          <div className="text-sm text-slate-500">
            Step <span className="font-semibold">1</span> of <span className="font-semibold">5</span>
          </div>
          <h1 className="text-2xl font-extrabold text-slate-800 mt-1">Select Document</h1>
        </div>

        <label className="block mb-6">
          <span className="text-sm text-slate-600">Choose ID Type</span>
          <select
            className="mt-2 w-full border border-slate-200 rounded-xl p-3 focus:ring-2 focus:ring-blue-200"
            value={docType}
            onChange={(e) => setDocType(e.target.value)}
          >
            <option>Aadhaar Card</option>
            <option>PAN Card</option>
            <option>Passport</option>
            <option>Voter ID</option>
          </select>
        </label>

        <button
          type="submit"
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-xl flex justify-center items-center gap-2 shadow-lg"
        >
          Next <RiArrowRightLine size={22} />
        </button>
      </motion.form>
    </div>
  );
}
