// src/pages/kyc-step2.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiArrowRightLine, RiArrowLeftLine } from "react-icons/ri";
import { motion } from "framer-motion";
import ProgressBar from "../components/ProgressBar";

const STORAGE_KEY_PERSONAL = "dk_kyc_personal";
const STORAGE_KEY_DOCINFO = "dk_kyc_docinfo";

export default function KYCStep2() {
  const navigate = useNavigate();
  const [docType, setDocType] = useState("Aadhaar");
  const [docNumber, setDocNumber] = useState("");
  const [error, setError] = useState("");

  useEffect(()=> {
    try {
      const saved = JSON.parse(localStorage.getItem(STORAGE_KEY_DOCINFO) || "{}");
      if (saved) {
        setDocType(saved.docType || "Aadhaar");
        setDocNumber(saved.docNumber || "");
      }
    } catch {}
  }, []);

  const handleBack = () => navigate("/kyc-step1");

  const handleNext = (e) => {
    e?.preventDefault();
    setError("");
    if (!docNumber || docNumber.trim().length < 3) {
      setError("Please enter a valid document number.");
      return;
    }

    try {
      localStorage.setItem(STORAGE_KEY_DOCINFO, JSON.stringify({ docType, docNumber }));
    } catch {}

    navigate("/kyc-step3");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form onSubmit={handleNext} initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4 }} className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-2xl border border-gray-100">
        <div className="mb-4"><ProgressBar step={2} total={5} /></div>

        <h2 className="text-2xl font-extrabold mb-4">Document details</h2>
        <p className="text-sm text-slate-500 mb-6">Select which ID you'll submit and provide the document number.</p>

        <label className="block text-sm font-medium text-slate-700 mb-2">Document type</label>
        <select value={docType} onChange={e=>setDocType(e.target.value)} className="w-full border border-slate-200 rounded-lg px-4 py-3 mb-4">
          <option>Aadhaar</option>
          <option>PAN</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>

        <input value={docNumber} onChange={e=>setDocNumber(e.target.value)} placeholder="Enter document number" className="w-full border p-3 rounded-lg mb-4" />

        <div className="flex gap-3">
          <button type="button" onClick={handleBack} className="flex items-center gap-2 px-4 py-2 rounded-lg border border-slate-200 bg-white"> <RiArrowLeftLine /> Back</button>
          <button type="submit" className="ml-auto inline-flex items-center gap-2 px-5 py-3 rounded-lg bg-blue-700 text-white font-medium shadow"> Next <RiArrowRightLine /></button>
        </div>

        {error && <div className="text-red-600 mt-3">{error}</div>}
      </motion.form>
    </div>
  );
}
