// src/pages/dashboard.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  RiSearchLine,
  RiNotification3Line,
  RiUser3Line,
  RiLogoutBoxRLine,
  RiHomeLine,
  RiWalletLine,
  RiFileListLine,
  RiShieldCheckLine,
  RiBankCardLine,
} from "react-icons/ri";
import API, { setToken as setApiToken } from "../services/api";
import HDFCLogo from "../assets/hdfc.png";

const maskAccount = (acc) => {
  if (!acc) return "XXXX-XXXX-XXXX";
  const s = String(acc).replace(/\s+/g, "");
  return s.length <= 4 ? "****" : `XXXX-XXXX-${s.slice(-4)}`;
};
const statusColor = (s) => {
  if (!s) return "bg-gray-100 text-gray-800";
  const key = String(s).toLowerCase();
  if (key.includes("completed") || key.includes("approve")) return "bg-green-100 text-green-800";
  if (key.includes("reject")) return "bg-red-100 text-red-800";
  if (key.includes("start")) return "bg-yellow-50 text-yellow-800";
  return "bg-indigo-50 text-indigo-800";
};

export default function Dashboard() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState({
    name: "",
    email: "",
    mobile: "",
    kycId: "N/A",
    kycStatus: "Completed", // default to Completed per your request
    accountNo: "",
    balance: null,
  });

  useEffect(() => {
    // restore axios header if token present
    const token = localStorage.getItem("token");
    if (token) {
      try { setApiToken(token); } catch (e) {}
    }

    const load = async () => {
      setLoading(true);
      try {
        // attempt to fetch from backend
        const res = await API.get("/kyc/dashboard");
        const d = res.data || {};

        // Use server values if present, otherwise fall back to localStorage
        setUser({
          name: d.fullName || d.name || localStorage.getItem("userName") || "",
          email: d.email || localStorage.getItem("userEmail") || "",
          mobile: d.mobile || localStorage.getItem("userMobile") || "",
          kycId: d.kycId || localStorage.getItem("userKycId") || "N/A",
          // ensure status is Completed if local says so or backend says so
          kycStatus: d.status || localStorage.getItem("userKycStatus") || "Completed",
          accountNo: d.accountNo || localStorage.getItem("userAccountNo") || "",
          balance: typeof d.balance === "number" ? d.balance : (localStorage.getItem("userBalance") ? Number(localStorage.getItem("userBalance")) : null),
        });
      } catch (err) {
        // fallback to localStorage (we persist Completed there after confirmation)
        setUser({
          name: localStorage.getItem("userName") || "",
          email: localStorage.getItem("userEmail") || "",
          mobile: localStorage.getItem("userMobile") || "",
          kycId: localStorage.getItem("userKycId") || "N/A",
          kycStatus: localStorage.getItem("userKycStatus") || "Completed",
          accountNo: localStorage.getItem("userAccountNo") || "",
          balance: localStorage.getItem("userBalance") ? Number(localStorage.getItem("userBalance")) : null,
        });
        console.warn("dashboard fetch failed:", err?.message || err);
      } finally {
        setLoading(false);
      }
    };

    load();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    // keep profile data if you want; here we leave other keys
    try {
      navigate("/", { replace: true });
      setTimeout(() => { if (window.location.pathname !== "/") window.location.href = "/"; }, 120);
    } catch {
      window.location.href = "/";
    }
  };

  const goHome = () => {
    try {
      navigate("/", { replace: true });
      setTimeout(() => { if (window.location.pathname !== "/") window.location.href = "/"; }, 120);
    } catch {
      window.location.href = "/";
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="w-full bg-[#0047B3] text-white shadow sticky top-0 z-20">
        <div className="max-w-7xl mx-auto px-6 py-3 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <img src={HDFCLogo} alt="HDFC" className="h-8 w-auto object-contain" />
            <div>
              <div className="text-xs opacity-80">Welcome back</div>
              <div className="text-lg font-semibold">{user.name || "Customer"}</div>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="relative hidden md:block">
              <input className="w-72 py-2 pl-3 pr-10 rounded-full text-sm" placeholder="Search banking services..." />
              <RiSearchLine className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500" />
            </div>

            <button onClick={goHome} title="Home" className="p-2 rounded-full bg-white/10 hover:bg-white/20"><RiHomeLine /></button>
            <button title="Notifications" className="p-2 rounded-full bg-white/10 hover:bg-white/20"><RiNotification3Line /></button>
            <button title="Profile" className="p-2 rounded-full bg-white/10 hover:bg-white/20"><RiUser3Line /></button>
            <button onClick={handleLogout} title="Logout" className="p-2 rounded-full bg-white"><RiLogoutBoxRLine className="text-[#d33]" /></button>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-10">
        <section className="bg-white rounded-lg shadow p-6 mb-8 border border-gray-200">
          <div className="flex flex-col md:flex-row justify-between gap-6">
            <div className="flex-1">
              <h3 className="text-lg font-semibold mb-3">KYC Details</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                <div>
                  <div className="text-sm text-gray-600">KYC Status</div>
                  <div className={`inline-block mt-1 px-3 py-1 rounded-full text-sm font-medium ${statusColor(user.kycStatus)}`}>
                    {user.kycStatus}
                  </div>
                </div>

                <div>
                  <div className="text-sm text-gray-600">KYC ID</div>
                  <div className="mt-1 font-medium">{user.kycId}</div>
                </div>

                <div>
                  <div className="text-sm text-gray-600">Name</div>
                  <div className="mt-1 font-medium">{user.name || "—"}</div>
                </div>

                <div>
                  <div className="text-sm text-gray-600">Email</div>
                  <div className="mt-1 font-medium">{user.email || "—"}</div>
                </div>

                <div>
                  <div className="text-sm text-gray-600">Mobile</div>
                  <div className="mt-1 font-medium">{user.mobile || "—"}</div>
                </div>

                <div>
                  <div className="text-sm text-gray-600">Account</div>
                  <div className="mt-1 font-medium">{maskAccount(user.accountNo)}</div>
                </div>
              </div>
            </div>

            <div className="w-full md:w-1/3 flex flex-col justify-between">
              <div className="bg-gradient-to-r from-[#0047B3] to-[#007BFF] text-white p-4 rounded-lg shadow">
                <div className="text-sm opacity-80">Primary Account</div>
                <div className="text-2xl font-bold mt-1">₹ {user.balance !== null ? user.balance.toLocaleString() : "—"}</div>
                <div className="text-xs opacity-80">Available balance</div>

                <div className="mt-4 flex gap-3">
                  <button className="bg-white text-[#0047B3] px-4 py-2 rounded font-medium">Transfer</button>
                  <button className="bg-white/20 text-white px-4 py-2 rounded font-medium">Statement</button>
                </div>
              </div>

              <div className="mt-4 bg-white border border-gray-100 p-3 rounded text-sm">
                <div className="font-medium">Quick actions</div>
                <div className="mt-2 flex gap-2">
                  <button className="flex-1 py-2 rounded border border-gray-200">Request Card</button>
                  <button className="flex-1 py-2 rounded border border-gray-200">Open FD</button>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Services grid */}
        <section className="mb-8">
          <h4 className="text-lg font-semibold mb-4">Banking Services</h4>
          <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6">
            <ServiceCard title="Accounts" icon={<RiWalletLine size={22} />} />
            <ServiceCard title="Deposits" icon={<RiFileListLine size={22} />} />
            <ServiceCard title="Loans" icon={<RiShieldCheckLine size={22} />} />
            <ServiceCard title="Cards" icon={<RiBankCardLine size={22} />} />
            <ServiceCard title="Insurance" icon={<RiShieldCheckLine size={22} />} />
            <ServiceCard title="Payments" icon={<RiFileListLine size={22} />} />
            <ServiceCard title="UPI & QR" icon={<RiWalletLine size={22} />} />
            <ServiceCard title="Statements" icon={<RiFileListLine size={22} />} />
          </div>
        </section>
      </main>
    </div>
  );
}

function ServiceCard({ title, icon }) {
  return (
    <button type="button" className="bg-white border border-gray-200 rounded-lg p-6 text-center hover:shadow-md transition flex flex-col items-center gap-3">
      <div className="text-[#0047B3] bg-[#e9f1ff] rounded p-3">{icon}</div>
      <div className="font-semibold text-gray-800">{title}</div>
    </button>
  );
}
