// src/pages/kyc-step5.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiDownloadCloudLine, RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";
import ProgressBar from "../components/ProgressBar";

/**
 * This step shows final success and allows the user to download a confirmation.
 * For reliability we create a simple HTML blob and trigger a download. If you
 * want a stylized PDF avoid window.open and use jspdf/html2canvas server-side or client-side instead.
 */

const PERSONAL_KEY = "dk_kyc_personal";
const DOC_KEY = "dk_kyc_docinfo";

export default function KYCStep5() {
  const navigate = useNavigate();
  const [personal, setPersonal] = useState({});
  const [docinfo, setDocinfo] = useState({});
  const [subTime, setSubTime] = useState("");

  useEffect(() => {
    try {
      const p = JSON.parse(localStorage.getItem(PERSONAL_KEY) || "{}");
      const d = JSON.parse(localStorage.getItem(DOC_KEY) || "{}");
      setPersonal(p || {});
      setDocinfo(d || {});
    } catch (e) {
      setPersonal({});
      setDocinfo({});
    }
    setSubTime(localStorage.getItem("dk_kyc_submitted_at") || new Date().toISOString());
    // also mark userKycStatus Completed globally
    localStorage.setItem("userKycStatus", "Completed");
  }, []);

  const handleGoDashboard = () => {
    // optionally ensure KYC completed
    localStorage.setItem("userKycStatus", "Completed");
    // copy personal data to user keys used by dashboard
    if (personal?.form) {
      localStorage.setItem("userName", personal.form.fullName || "");
    }
    navigate("/dashboard");
  };

  const buildConfirmationHtml = () => {
    const applicant = personal?.form?.fullName || "Customer";
    const kycId = localStorage.getItem("userKycId") || "N/A";
    const accountNo = localStorage.getItem("userAccountNo") || "Not assigned";
    const status = "Completed";
    const submitted = new Date(subTime).toLocaleString();

    const docLine = `${docinfo?.docType || "Document"} / ${docinfo?.docNumber ? "********" + docinfo.docNumber.slice(-4) : "—"}`;

    return `
      <!doctype html>
      <html>
        <head>
          <meta charset="utf-8" />
          <title>KYC Confirmation - ${applicant}</title>
          <style>
            body{font-family: Arial, Helvetica, sans-serif; padding:20px; color:#0f172a}
            .card{max-width:720px; margin:0 auto; border:1px solid #e6eefc; padding:24px; border-radius:10px}
            .hdr{display:flex; align-items:center; gap:12px}
            .logo{height:48px}
            .title{font-size:20px; font-weight:700; color:#0b57b6}
            .box{background:#f8fbff; padding:18px; margin-top:18px; border-radius:8px}
            .row{display:flex; justify-content:space-between; padding:10px 0; border-bottom:1px dashed #e6eefc}
            .label{color:#334155; width:180px}
            .val{font-weight:600}
          </style>
        </head>
        <body>
          <div class="card">
            <div class="hdr">
              <img src="" alt="HDFC" class="logo" />
              <div>
                <div class="title">Digital KYC — KYC Confirmation</div>
                <div style="color:#475569; font-size:13px">Congratulations, ${applicant} — your KYC is ${status}.</div>
              </div>
            </div>

            <div class="box">
              <div class="row"><div class="label">Applicant</div><div class="val">${applicant}</div></div>
              <div class="row"><div class="label">KYC ID</div><div class="val">${kycId}</div></div>
              <div class="row"><div class="label">Account No</div><div class="val">${accountNo}</div></div>
              <div class="row"><div class="label">Status</div><div class="val">${status}</div></div>
              <div class="row"><div class="label">Submitted at</div><div class="val">${submitted}</div></div>
              <div class="row"><div class="label">Document</div><div class="val">${docLine}</div></div>
            </div>

            <p style="color:#6b7280; margin-top:16px; font-size:13px">Generated by Digital KYC System.</p>
          </div>
        </body>
      </html>
    `;
  };

  const triggerDownload = (filename, content, mime = "text/html") => {
    try {
      const blob = new Blob([content], { type: mime });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = filename;
      a.style.display = "none";
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } catch (err) {
      // fallback: open in new tab (may be blocked)
      window.open("data:" + mime + ";base64," + btoa(unescape(encodeURIComponent(content))));
    }
  };

  const handleDownload = () => {
    const html = buildConfirmationHtml();
    // prefer a .html download (user can Print->Save as PDF). If you want direct PDF, integrate jspdf/html2canvas.
    triggerDownload("KYC-Confirmation.html", html, "text/html");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.35 }} className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl border border-gray-100 text-center">
        <div className="mb-4"><ProgressBar step={5} total={5} /></div>

        <div className="text-green-600 text-4xl mb-3">✓</div>
        <h2 className="text-2xl font-bold mb-2">KYC Submitted</h2>
        <p className="text-slate-600 mb-6">Thanks {personal?.form?.fullName || "Customer"} — your KYC is submitted.</p>

        <div className="flex gap-4 justify-center">
          <button onClick={handleDownload} className="px-6 py-3 rounded-lg bg-blue-600 text-white inline-flex items-center gap-2">
            <RiDownloadCloudLine /> Download Confirmation
          </button>

          <button onClick={handleGoDashboard} className="px-6 py-3 rounded-lg bg-green-600 text-white inline-flex items-center gap-2">
            Go to Dashboard <RiArrowRightLine />
          </button>
        </div>

        <div className="text-xs text-slate-400 mt-4">Profile available</div>
      </motion.div>
    </div>
  );
}
