import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { RiArrowRightLine } from "react-icons/ri";

export default function KYCStep1() {
  const [docType, setDocType] = useState("Aadhaar Card");
  const navigate = useNavigate();

  const handleNext = () => {
    navigate("/kyc-step2");
  };

  return (
    <div className="h-full w-full flex justify-center items-center">
      <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-lg border border-gray-200">
        <h1 className="text-3xl font-bold mb-6 text-gray-800 text-center">Step 1: Select Document</h1>

        <select
          className="w-full border-gray-300 border rounded-lg p-3 mb-6 focus:ring-2 focus:ring-blue-500 focus:outline-none"
          value={docType}
          onChange={e => setDocType(e.target.value)}
        >
          <option>Aadhaar Card</option>
          <option>PAN Card</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>

        <button
          onClick={handleNext}
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-lg flex justify-center items-center gap-2 transition duration-200"
        >
          Next <RiArrowRightLine size={20} />
        </button>
      </div>
    </div>
  );
}
