// src/pages/kyc-step5.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiDownloadCloudLine, RiCheckLine, RiArrowRightLine } from "react-icons/ri";
import HDFCLogo from "../assets/hdfc.png";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

/* small helpers */
const escapeHtml = (s) => (s ? String(s).replace(/&/g,"&amp;").replace(/</g,"&lt;").replace(/>/g,"&gt;") : "");
const maskAccount = (acc) => {
  if (!acc) return "Not assigned";
  const s = String(acc).replace(/\s+/g,"");
  return s.length <= 4 ? "****" : `XXXX-XXXX-${s.slice(-4)}`;
};
const maskDocNum = (d) => {
  if (!d) return "—";
  const s = String(d);
  if (s.length <= 4) return "****";
  return `${"*".repeat(Math.max(0, s.length - 4))}${s.slice(-4)}`;
};
const fmtDate = (iso) => {
  try { return new Date(iso).toLocaleString(); } catch { return new Date().toLocaleString(); }
};

function buildHtml({ applicant, kycId, accountNo, status, submittedAt, docType, docNumber, logoUrl, photoDataUrl }) {
  const photoHtml = photoDataUrl ? `<div style="margin-top:12px"><img src="${photoDataUrl}" style="width:96px;height:120px;object-fit:cover;border-radius:6px;border:1px solid #e6e6e6" /></div>` : "";
  return `<!doctype html><html><head><meta charset="utf-8"/><meta name="viewport" content="width=device-width,initial-scale=1" />
  <style>
    body{font-family:Arial,Helvetica,sans-serif;background:#f6f9fb;margin:0;padding:20px;color:#12202a}
    .box{max-width:760px;margin:0 auto;background:#fff;border-radius:10px;padding:26px;border:1px solid #e8eef6}
    .header{display:flex;gap:12px;align-items:center}
    .logo{width:56px;height:56px;border-radius:8px;overflow:hidden;display:flex;align-items:center;justify-content:center}
    .hero{margin-top:16px;padding:16px;background:linear-gradient(90deg,#f0f7ff,#f6fbff);border-radius:8px}
    .hero h1{margin:0;font-size:20px;color:#0b4aa2}
    .grid{display:flex;gap:16px;margin-top:18px;flex-wrap:wrap}
    .card{flex:1 1 320px;background:#fff;padding:12px;border-radius:8px;border:1px solid #f0f4f8}
    table{width:100%;border-collapse:collapse;margin-top:6px}
    td{padding:8px 6px;border-bottom:1px dashed #eef2f7}
    td.label{width:36%;font-weight:700;color:#0b3b63}
    .muted{color:#6b7a89;font-size:13px}
    .footer{margin-top:14px;color:#6b7a89;font-size:13px}
  </style>
  </head><body>
    <div class="box">
      <div class="header">
        <div class="logo"><img src="${logoUrl}" style="height:100%;width:auto;display:block" /></div>
        <div><div style="font-size:13px;color:#6b7a89">Digital KYC</div><div style="font-size:18px;font-weight:800;color:#0b2540">KYC Confirmation</div></div>
      </div>

      <div class="hero">
        <h1>Congratulations, ${escapeHtml(applicant)}!</h1>
        <div style="color:#44566b;margin-top:6px">Your KYC submission is received and is under verification. Keep this confirmation for your records.</div>
      </div>

      <div class="grid">
        <div class="card">
          <table>
            <tr><td class="label">Applicant</td><td>${escapeHtml(applicant)}</td></tr>
            <tr><td class="label">KYC ID</td><td>${escapeHtml(kycId || "N/A")}</td></tr>
            <tr><td class="label">Account No</td><td>${escapeHtml(accountNo ? maskAccount(accountNo) : "Not assigned")}</td></tr>
            <tr><td class="label">Status</td><td>${escapeHtml(status)}</td></tr>
            <tr><td class="label">Submitted at</td><td>${escapeHtml(fmtDate(submittedAt))}</td></tr>
            <tr><td class="label">Document</td><td>${escapeHtml(docType)} / ${escapeHtml(maskDocNum(docNumber))}</td></tr>
          </table>
        </div>

        <div class="card">
          <div style="font-weight:700;color:#0b2540">Important</div>
          <div class="muted" style="margin-top:8px">Please retain this confirmation. Full document numbers were submitted to server securely and are masked here for privacy.</div>
          ${photoHtml}
        </div>
      </div>

      <div class="footer">Generated by Digital KYC System.</div>
    </div>
  </body></html>`;
}

export default function KYCStep5() {
  const navigate = useNavigate();
  const [personal, setPersonal] = useState(null);
  const [docData, setDocData] = useState(null);
  const [photoData, setPhotoData] = useState(null);
  const [completeResp, setCompleteResp] = useState(null);
  const [loadingPdf, setLoadingPdf] = useState(false);

  useEffect(() => {
    try { setPersonal(JSON.parse(localStorage.getItem("kycPersonal") || "null")); } catch { setPersonal(null); }
    try { setDocData(JSON.parse(localStorage.getItem("kycDocument") || "null")); } catch { setDocData(null); }
    setPhotoData(localStorage.getItem("kycPhoto") || null);
    try { setCompleteResp(JSON.parse(localStorage.getItem("kycCompleteResponse") || "null")); } catch { setCompleteResp(null); }
  }, []);

  const handleDownloadPdf = async () => {
    if (loadingPdf) return;
    setLoadingPdf(true);

    try {
      // remove any previous leftover container (safe cleanup)
      const existing = document.getElementById("__kyc_pdf_container");
      if (existing) existing.remove();

      const applicant = personal?.fullName || personal?.name || "Customer";
      const kycId = completeResp?.kycId || "N/A";
      const accountNo = completeResp?.accountNo || null;
      const status = completeResp?.status || "Submitted";
      const submittedAt = completeResp?.submittedAt || new Date().toISOString();
      const docType = docData?.docType || "—";
      const docNumber = docData?.docNumber || "—";
      const logoUrl = HDFCLogo;
      const photoUrl = photoData || "";

      const html = buildHtml({ applicant, kycId, accountNo, status, submittedAt, docType, docNumber, logoUrl, photoDataUrl: photoUrl });

      // create a hidden container (with an id so old copies get cleaned up)
      const container = document.createElement("div");
      container.id = "__kyc_pdf_container";
      container.style.position = "fixed";
      container.style.left = "-99999px";
      container.style.top = "0";
      container.style.width = "800px";
      container.innerHTML = html;
      document.body.appendChild(container);

      // wait for images in the container to load
      const imgs = Array.from(container.querySelectorAll("img"));
      await Promise.all(imgs.map(img => img.complete ? Promise.resolve() : new Promise(res => { img.onload = img.onerror = res; })));

      // render using html2canvas
      const canvas = await html2canvas(container, { scale: 2, useCORS: true, logging: false });

      // prepare pdf
      const imgData = canvas.toDataURL("image/jpeg", 0.95);
      const pdf = new jsPDF('p', 'mm', 'a4');
      const pageW = pdf.internal.pageSize.getWidth();
      const pageH = pdf.internal.pageSize.getHeight();
      const margin = 10;
      const imgWidth = pageW - margin * 2;
      const pxToMm = imgWidth / canvas.width;
      const imgHeight = canvas.height * pxToMm;

      if (imgHeight <= pageH - margin * 2) {
        pdf.addImage(imgData, 'JPEG', margin, margin, imgWidth, imgHeight);
      } else {
        // multi-page: slice canvas
        const sliceHeightPx = Math.floor((pageH - margin * 2) / pxToMm);
        const totalPages = Math.ceil(canvas.height / sliceHeightPx);
        for (let i = 0; i < totalPages; i++) {
          const sliceCanvas = document.createElement('canvas');
          sliceCanvas.width = canvas.width;
          sliceCanvas.height = Math.min(sliceHeightPx, canvas.height - i * sliceHeightPx);
          const ctx = sliceCanvas.getContext('2d');
          ctx.fillStyle = '#ffffff';
          ctx.fillRect(0, 0, sliceCanvas.width, sliceCanvas.height);
          ctx.drawImage(canvas, 0, i * sliceHeightPx, canvas.width, sliceCanvas.height, 0, 0, canvas.width, sliceCanvas.height);
          const pageImg = sliceCanvas.toDataURL('image/jpeg', 0.95);
          if (i > 0) pdf.addPage();
          const pageImgHeight = sliceCanvas.height * pxToMm;
          pdf.addImage(pageImg, 'JPEG', margin, margin, imgWidth, pageImgHeight);
        }
      }

      // force download (this does not open a new tab)
      const filename = `KYC-confirmation-${(applicant || 'customer').replace(/\s+/g, '_')}.pdf`;
      pdf.save(filename);

      // cleanup container
      container.remove();
    } catch (err) {
      console.error("PDF generation failed:", err);
      alert("Could not generate PDF. Try again or refresh the page.");
    } finally {
      setLoadingPdf(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-8">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-xl p-8 text-center">
        <div className="flex items-center justify-center mb-4">
          <div className="rounded-full bg-green-50 p-4 inline-flex items-center justify-center">
            <RiCheckLine className="text-green-600" size={36} />
          </div>
        </div>

        <h2 className="text-2xl font-extrabold mb-2">KYC Submitted</h2>
        <p className="text-gray-600 mb-6">Thanks {personal?.fullName || "Customer"} — your KYC is submitted.</p>

        <div className="flex gap-4 justify-center mb-4">
          <button
            type="button"
            onClick={handleDownloadPdf}
            disabled={loadingPdf}
            className={`inline-flex items-center gap-2 px-5 py-3 bg-blue-700 text-white rounded-lg shadow hover:opacity-95 ${loadingPdf ? "opacity-70 cursor-not-allowed" : ""}`}
          >
            <RiDownloadCloudLine /> {loadingPdf ? "Preparing PDF..." : "Download Confirmation"}
          </button>

          <button
            type="button"
            onClick={() => navigate("/dashboard")}
            className="inline-flex items-center gap-2 px-5 py-3 bg-green-600 text-white rounded-lg shadow hover:opacity-95"
          >
            Go to Dashboard <RiArrowRightLine />
          </button>
        </div>

        <div className="text-sm text-gray-400">Profile {personal ? "available" : "not available"}</div>
      </div>
    </div>
  );
}
