/* eslint-disable */
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowLeftLine, RiArrowRightLine, RiEditLine, RiCheckLine } from "react-icons/ri";
import API from "../services/api";

/**
 Expected localStorage keys used by previous steps:
  - dk_kyc_step1_v1  -> { name, dob, fatherName, address, gender, email, mobile, selfieDataUrl? }
  - dk_kyc_step2_v2  -> { addrType, addrNumber, idType, idNumber }
  - dk_kyc_step3_files -> optional { addressFileName, identityFileName, passportPhotoName }
*/

const STEP1_KEY = "dk_kyc_step1_v1";
const STEP2_KEY = "dk_kyc_step2_v2";
const STEP3_FILES_KEY = "dk_kyc_step3_files";

function maskNumber(val) {
  if (!val) return "—";
  const s = String(val);
  if (s.length <= 4) return "*".repeat(4 - s.length > 0 ? 4 - s.length : 0) + s;
  const visible = s.slice(-4);
  return s.slice(0, s.length - 4).replace(/./g, "*") + visible;
}

export default function KYCStep4() {
  const navigate = useNavigate();

  const [personal, setPersonal] = useState({});
  const [docs, setDocs] = useState({});
  const [files, setFiles] = useState({});
  const [loading, setLoading] = useState(false);
  const [statusMsg, setStatusMsg] = useState("");

  useEffect(() => {
    // load step1 personal data
    try {
      const s1 = JSON.parse(localStorage.getItem(STEP1_KEY) || "{}");
      setPersonal(s1 || {});
    } catch {
      setPersonal({});
    }
    // load step2 docs
    try {
      const s2 = JSON.parse(localStorage.getItem(STEP2_KEY) || "{}");
      setDocs(s2 || {});
    } catch {
      setDocs({});
    }
    // optional files saved by step3 (if you stored them)
    try {
      const s3 = JSON.parse(localStorage.getItem(STEP3_FILES_KEY) || "{}");
      setFiles(s3 || {});
    } catch {
      setFiles({});
    }
  }, []);

  const handleEditPersonal = () => {
    navigate("/kyc-step1");
  };

  const handleEditDocs = () => {
    navigate("/kyc-step2");
  };

  const handleEditFiles = () => {
    navigate("/kyc-step3");
  };

  const handleBack = () => {
    navigate("/kyc-step3");
  };

  const handleConfirm = async () => {
    setStatusMsg("");
    setLoading(true);
    try {
      // Call backend finalize endpoint - adjust path if different
      const res = await API.post("/kyc/submit");
      // optionally you can save server response to localStorage for step5
      try {
        localStorage.setItem("dk_kyc_summary", JSON.stringify(res.data || {}));
      } catch {}
      navigate("/kyc-step5");
    } catch (err) {
      const msg = err?.response?.data?.message || err?.message || "Unable to submit. Try again.";
      setStatusMsg(msg);
    } finally {
      setLoading(false);
    }
  };

  // friendly values for display
  const displayName = personal.name || "Customer";
  const displayDOB = personal.dob || "—";
  const displayFather = personal.fatherName || "—";
  const displayAddress = personal.address || "—";
  const displayGender = personal.gender || "—";

  const addrType = docs.addrType || "—";
  const addrNumber = docs.addrNumber || "";
  const idType = docs.idType || "—";
  const idNumber = docs.idNumber || "";

  const addressFile = files.addressFileName || files.addressFile || null;
  const identityFile = files.identityFileName || files.identityFile || null;
  const passportPhoto = personal.selfieDataUrl || files.passportPhotoName || null;

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form
        onSubmit={(e) => { e.preventDefault(); handleConfirm(); }}
        initial={{ opacity: 0, y: 8 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-3xl bg-white p-8 rounded-2xl shadow-2xl border"
      >
        <div className="flex items-start justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">Step <span className="font-semibold">4</span> of <span className="font-semibold">5</span></div>
            <h2 className="text-2xl font-extrabold text-slate-800">Review & confirm your details</h2>
            <p className="mt-1 text-sm text-slate-500">
              Please verify the details below before submission. You can edit any item if something looks wrong.
            </p>
          </div>

          <div className="w-36 text-right">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden mb-2">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: "80%" }} />
            </div>
            <div className="text-xs text-slate-400">Progress: 80%</div>
          </div>
        </div>

        {/* Personal details card */}
        <div className="bg-white border rounded-lg shadow-sm p-6 mb-4">
          <div className="flex justify-between items-start">
            <div className="flex items-center gap-3">
              <div className="text-lg font-semibold">Personal details</div>
              <div className="text-sm text-green-600 flex items-center gap-1">
                <RiCheckLine /> <span>Completed</span>
              </div>
            </div>
            <div>
              <button type="button" onClick={handleEditPersonal} className="inline-flex items-center gap-2 px-3 py-2 border rounded-md text-sm">
                <RiEditLine /> Edit
              </button>
            </div>
          </div>

          <div className="mt-4 grid grid-cols-1 md:grid-cols-6 gap-4">
            <div className="md:col-span-5">
              <div className="text-sm text-slate-600"><strong>Name:</strong> <span className="text-slate-800">{displayName}</span></div>
              <div className="text-sm text-slate-600 mt-2"><strong>DOB:</strong> <span className="text-slate-800">{displayDOB}</span></div>
              <div className="text-sm text-slate-600 mt-2"><strong>Father's Name:</strong> <span className="text-slate-800">{displayFather}</span></div>
              <div className="text-sm text-slate-600 mt-2"><strong>Address:</strong> <span className="text-slate-800">{displayAddress}</span></div>
              <div className="text-sm text-slate-600 mt-2"><strong>Gender:</strong> <span className="text-slate-800">{displayGender}</span></div>
            </div>

            <div className="md:col-span-1 flex justify-end">
              {passportPhoto ? (
                <img src={passportPhoto} alt="passport" className="w-24 h-28 object-cover rounded shadow-sm border" />
              ) : (
                <div className="w-24 h-28 bg-slate-100 rounded flex items-center justify-center text-slate-400">No photo</div>
              )}
            </div>
          </div>
        </div>

        {/* Selected document card */}
        <div className="bg-white border rounded-lg shadow-sm p-6 mb-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-3">
              <div className="text-lg font-semibold">Selected document</div>
              <div className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> <span>Selected</span></div>
            </div>

            <button type="button" onClick={handleEditDocs} className="inline-flex items-center gap-2 px-3 py-2 border rounded-md text-sm">
              <RiEditLine /> Edit
            </button>
          </div>

          <div className="mt-4">
            <div className="text-sm text-slate-700 font-medium">{addrType || "—"}</div>
            <div className="text-xs text-slate-400 mt-1">This is the document type you chose in Step 2.</div>
          </div>
        </div>

        {/* Document number card */}
        <div className="bg-white border rounded-lg shadow-sm p-6 mb-4">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-3">
              <div className="text-lg font-semibold">Document number</div>
              <div className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> <span>Provided</span></div>
            </div>

            <button type="button" onClick={handleEditDocs} className="inline-flex items-center gap-2 px-3 py-2 border rounded-md text-sm">
              <RiEditLine /> Edit
            </button>
          </div>

          <div className="mt-4">
            <div className="text-lg font-semibold">{addrNumber ? maskNumber(addrNumber) : "—"}</div>
            <div className="text-xs text-slate-400 mt-1">Masked in UI for privacy. Full value submitted to verification service.</div>
          </div>
        </div>

        {/* Uploaded file card */}
        <div className="bg-white border rounded-lg shadow-sm p-6 mb-6">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-3">
              <div className="text-lg font-semibold">Uploaded file</div>
              <div className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> <span>Uploaded</span></div>
            </div>

            <div className="flex items-center gap-3">
              <button type="button" onClick={handleEditFiles} className="px-3 py-2 border rounded-md text-sm">Change</button>
            </div>
          </div>

          <div className="mt-4 flex items-center gap-4">
            <div className="flex-1">
              <div className="font-medium text-slate-800">{identityFile ? idType : "No file uploaded"}</div>
              <div className="text-xs text-slate-400 mt-1">The file you uploaded in Step 3 will be used to verify the document.</div>
            </div>
            <div>
              {identityFile ? (
                // if server path (starts with http or /) use it directly, else show filename
                (identityFile.startsWith && identityFile.startsWith("data:")) ? (
                  <img src={identityFile} alt="doc" className="w-24 h-16 object-cover rounded border" />
                ) : (
                  <div className="text-sm text-slate-600">{identityFile}</div>
                )
              ) : null}
            </div>
          </div>
        </div>

        {statusMsg && <div className="text-sm text-rose-600 mb-4">{statusMsg}</div>}

        <div className="flex items-center gap-3">
          <button onClick={handleBack} type="button" className="px-4 py-2 rounded border bg-white inline-flex items-center gap-2">
            <RiArrowLeftLine /> Back
          </button>

          <button
            type="submit"
            disabled={loading}
            className="ml-auto px-6 py-3 bg-green-600 text-white rounded inline-flex items-center gap-2"
          >
            {loading ? "Submitting..." : "Confirm"} <RiArrowRightLine />
          </button>
        </div>
      </motion.form>
    </div>
  );
}
