import { useState } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";
import { RiUploadCloudLine, RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";

export default function KYCStep3() {
  const [file, setFile] = useState(null);
  const [docType, setDocType] = useState("Aadhaar");
  const navigate = useNavigate();

  const handleUpload = async () => {
    if (!file) return alert("Select a file");
    const formData = new FormData();
    formData.append("document", file);
    formData.append("type", docType);

    try {
      const res = await API.post("/kyc/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      alert(`Upload successful! Attempts left: ${res.data.attemptsLeft}`);
      navigate("/kyc-step4");
    } catch (err) {
      alert(err.response?.data?.message || "Upload failed");
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gradient-to-br from-blue-50 to-white p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl border border-gray-200"
      >
        <h2 className="text-blue-700 font-bold text-xl mb-2 text-center">Step 3 of 5</h2>
        <h1 className="text-3xl font-extrabold mb-6 text-gray-800 text-center">Upload Document</h1>

        <select
          className="w-full border-gray-300 border rounded-xl p-3 mb-4 focus:ring-2 focus:ring-blue-500 focus:outline-none transition duration-200"
          value={docType}
          onChange={e => setDocType(e.target.value)}
        >
          <option>Aadhaar</option>
          <option>PAN</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>

        <label className="flex items-center justify-center w-full h-16 mb-6 border-2 border-dashed border-gray-300 rounded-xl cursor-pointer hover:border-blue-500 transition duration-200">
          <input type="file" className="hidden" onChange={e => setFile(e.target.files[0])} />
          {file ? file.name : "Click to upload file"} <RiUploadCloudLine className="ml-2 text-blue-500" />
        </label>

        <button
          onClick={handleUpload}
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-xl flex justify-center items-center gap-2 shadow-lg transition duration-300"
        >
          Upload & Next <RiArrowRightLine size={22} />
        </button>
      </motion.div>
    </div>
  );
}
