import { useNavigate } from "react-router-dom";
import { RiCheckLine, RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";

export default function KYCStep4() {
  const navigate = useNavigate();

  const handleConfirm = () => {
    navigate("/kyc-step5");
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gradient-to-br from-blue-50 to-white p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl border border-gray-200 text-center"
      >
        <h2 className="text-blue-700 font-bold text-xl mb-2 text-center">Step 4 of 5</h2>
        <h1 className="text-3xl font-extrabold mb-6 text-gray-800">Review Details</h1>
        <p className="mb-6 text-gray-600">Please review your details and confirm to proceed.</p>

        <ul className="text-left mb-6 space-y-2">
          <li className="flex items-center gap-2"><RiCheckLine className="text-green-500" /> Document type: Aadhaar</li>
          <li className="flex items-center gap-2"><RiCheckLine className="text-green-500" /> Document number: 1234XXXX</li>
        </ul>

        <button
          onClick={handleConfirm}
          className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3 rounded-xl flex justify-center items-center gap-2 shadow-lg transition duration-300"
        >
          Confirm <RiArrowRightLine size={22} />
        </button>
      </motion.div>
    </div>
  );
}
