// src/pages/dashboard.jsx
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import {
  RiHomeLine,
  RiLogoutBoxRLine,
  RiSearchLine,
  RiNotification3Line,
  RiUser3Line,
  RiFileListLine,
  RiBankLine,
  RiMoneyDollarCircleLine,
  RiShieldCheckLine,
  RiHandCoinLine,
} from "react-icons/ri";

// ---------------- Helper: Mask Account Number ----------------
function maskAccountNo(acc) {
  if (!acc) return "XXXX-XXXX-XXXX";
  const last4 = acc.slice(-4);
  return `XXXX-XXXX-${last4}`;
}

// ---------------- Helper: KYC Badge ----------------
function KycBadge({ status }) {
  if (!status) return null;

  const styles = {
    Approved: "bg-green-100 text-green-700",
    Rejected: "bg-red-100 text-red-700",
    Pending: "bg-yellow-100 text-yellow-700",
  };

  return (
    <span
      className={`px-4 py-1 rounded-full text-xs font-semibold ${styles[status] || "bg-gray-100 text-gray-600"}`}
    >
      {status}
    </span>
  );
}

// ---------------- Main Dashboard ----------------
export default function Dashboard() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gray-50 text-slate-900 flex flex-col">

      {/* ---------------- TOP NAVBAR ---------------- */}
      <header className="bg-[#0047B3] text-white py-4 px-8 shadow-md flex items-center justify-between">
        <div>
          <div className="text-xs opacity-90">Welcome back</div>
          <div className="text-xl font-semibold">{user?.name || "Customer"}</div>
          <div className="mt-1">
            <KycBadge status={user?.kycStatus} />
          </div>
        </div>

        <div className="flex items-center gap-4">
          {/* SEARCH */}
          <div className="relative">
            <RiSearchLine className="absolute left-3 top-3 text-slate-400" size={18} />
            <input
              placeholder="Search banking services..."
              className="pl-10 pr-4 py-2 rounded-lg text-black w-60 border border-gray-200"
            />
          </div>

          <RiNotification3Line size={22} className="cursor-pointer" />
          <RiUser3Line size={22} className="cursor-pointer" />

          {/* HOME */}
          <button
            onClick={() => navigate("/")}
            className="p-2 bg-white text-[#0047B3] rounded-lg shadow hover:opacity-90"
            title="Home"
          >
            <RiHomeLine size={18} />
          </button>

          {/* LOGOUT */}
          <button
            onClick={() => { logout(); navigate("/"); }}
            className="p-2 bg-red-600 text-white rounded-lg shadow hover:bg-red-700"
            title="Logout"
          >
            <RiLogoutBoxRLine size={18} />
          </button>
        </div>
      </header>

      {/* ---------------- MAIN CONTENT ---------------- */}
      <main className="max-w-6xl w-full mx-auto px-6 py-10 flex-1">

        {/* ----------- KYC Details Section ----------- */}
        <section className="bg-white rounded-2xl p-6 shadow border border-gray-100 mb-8">
          <h2 className="text-lg font-semibold mb-3">KYC Details</h2>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm text-slate-700">
            <div><strong>KYC Status:</strong> <KycBadge status={user?.kycStatus} /></div>
            <div><strong>KYC ID:</strong> {user?.kycId}</div>
            <div><strong>Name:</strong> {user?.name}</div>
            <div><strong>Email:</strong> {user?.email}</div>
            <div><strong>Mobile:</strong> {user?.mobile}</div>
          </div>
        </section>

        {/* ----------- Account Card ----------- */}
        <section className="bg-white rounded-2xl p-6 shadow border border-gray-100 mb-8">
          <h2 className="text-gray-600 text-sm font-medium">Primary Account</h2>

          <div className="text-3xl font-bold mt-1 text-gray-900">
            {user?.balance ?? "₹ —"}
          </div>

          <p className="text-gray-500 text-sm mt-2">
            Account: {maskAccountNo(user?.accountNo)}
          </p>

          <div className="flex gap-4 mt-6">
            <button className="bg-blue-700 text-white px-6 py-2 rounded-md hover:bg-blue-800 transition">
              Transfer
            </button>
            <button className="border border-gray-300 px-6 py-2 rounded-md hover:bg-gray-100 transition">
              Statement
            </button>
          </div>
        </section>

        {/* ----------- Services Grid ----------- */}
        <section>
          <h3 className="text-lg font-semibold mb-4">Banking Services</h3>

          <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6">

            <ServiceCard icon={<RiBankLine />} title="Accounts" />
            <ServiceCard icon={<RiMoneyDollarCircleLine />} title="Deposits" />
            <ServiceCard icon={<RiHandCoinLine />} title="Loans" />
            <ServiceCard icon={<RiFileListLine />} title="Investments" />
            <ServiceCard icon={<RiShieldCheckLine />} title="Insurance" />
            <ServiceCard icon={<RiMoneyDollarCircleLine />} title="Payments" />
            <ServiceCard icon={<RiHandCoinLine />} title="UPI & QR" />
            <ServiceCard icon={<RiFileListLine />} title="Statements" />

          </div>
        </section>

      </main>

      {/* FOOTER */}
      <footer className="text-center text-xs text-slate-500 py-4">
        © {new Date().getFullYear()} HDFC Digital Banking — All Rights Reserved.
      </footer>
    </div>
  );
}

// ---------------- Reusable Components ----------------

function ServiceCard({ icon, title }) {
  return (
    <div className="bg-white rounded-xl border border-gray-200 shadow hover:shadow-md p-6 text-center cursor-pointer transition">
      <div className="text-[#0047B3] mb-3" style={{ fontSize: "30px" }}>
        {icon}
      </div>
      <div className="font-semibold text-gray-800">{title}</div>
    </div>
  );
}
