// src/pages/kyc-step3.jsx
import { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import { motion } from "framer-motion";
import { RiUploadCloudLine, RiArrowRightLine, RiArrowLeftLine } from "react-icons/ri";

const MAX_BYTES = 5 * 1024 * 1024;

export default function KYCStep3() {
  const navigate = useNavigate();
  const inputRef = useRef();
  const [files, setFiles] = useState({ aadhaar: null, pan: null, passport: null, selfie: null });
  const [error, setError] = useState("");
  const [uploading, setUploading] = useState(false);
  const [attemptsLeft, setAttemptsLeft] = useState(null);
  const [progress] = useState(40); // step 3 of 5

  // load previously saved choices (optional)
  useEffect(() => {
    try {
      const raw = JSON.parse(localStorage.getItem("dk_uploaded_files") || "{}");
      setFiles(raw);
    } catch {}
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem("dk_uploaded_files", JSON.stringify(files));
    } catch {}
  }, [files]);

  const checkFile = (f) => {
    if (!f) return "No file";
    if (!["image/jpeg", "image/png", "application/pdf"].includes(f.type)) return "Only JPG, PNG, PDF allowed.";
    if (f.size > MAX_BYTES) return `Max file size ${Math.round(MAX_BYTES / (1024 * 1024))}MB`;
    return null;
  };

  const handleSelect = (e, key) => {
    const f = e.target.files?.[0];
    const reason = checkFile(f);
    if (reason) {
      setError(reason);
      return;
    }
    setError("");
    setFiles((s) => ({ ...s, [key]: f }));
  };

  const uploadSingle = async (key) => {
    if (!files[key]) throw new Error(`${key} not selected`);
    const fd = new FormData();
    fd.append("document", files[key]);
    fd.append("type", key);
    const res = await API.post("/kyc/upload", fd, { headers: { "Content-Type": "multipart/form-data" } });
    return res.data;
  };

  const handleUploadAll = async (e) => {
    e?.preventDefault();
    setError("");
    // Aadhaar & PAN must be present
    if (!files.aadhaar || !files.pan) {
      setError("Aadhaar and PAN documents are required to proceed.");
      return;
    }

    try {
      setUploading(true);
      setAttemptsLeft(null);

      // Upload Aadhaar
      const r1 = await uploadSingle("aadhaar");
      setAttemptsLeft(r1.attemptsLeft ?? null);

      // Upload PAN
      const r2 = await uploadSingle("pan");
      setAttemptsLeft(r2.attemptsLeft ?? attemptsLeft);

      // optional passport
      if (files.passport) {
        const r3 = await uploadSingle("passport");
        setAttemptsLeft(r3.attemptsLeft ?? attemptsLeft);
      }

      // now navigate to selfie/photo-match step
      navigate("/kyc-step4");
    } catch (err) {
      setError(err?.response?.data?.message || err.message || "Upload failed");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-blue-50">
      <motion.form
        onSubmit={handleUploadAll}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow"
      >
        <div className="flex justify-between items-start mb-6">
          <div>
            <div className="text-sm text-slate-500">Step 3 of 5</div>
            <h2 className="text-2xl font-bold">Upload Documents</h2>
            <p className="text-sm text-slate-500 mt-1">Upload clear photos or PDF scans. Max 5 MB each.</p>
          </div>
          <div className="text-sm text-slate-400">Progress: {progress}%</div>
        </div>

        <div className="space-y-4">
          <FileInput label="Aadhaar (required)" name="aadhaar" file={files.aadhaar} onChange={(e) => handleSelect(e, "aadhaar")} />
          <FileInput label="PAN (required)" name="pan" file={files.pan} onChange={(e) => handleSelect(e, "pan")} />
          <FileInput label="Passport (optional)" name="passport" file={files.passport} onChange={(e) => handleSelect(e, "passport")} />
          <FileInput label="Passport-size photo / Selfie (required)" name="selfie" file={files.selfie} onChange={(e) => handleSelect(e, "selfie")} />
        </div>

        {attemptsLeft !== null && <div className="mt-3 text-sm text-yellow-700">Attempts left (this stage): {attemptsLeft}</div>}
        {error && <div className="mt-3 text-sm text-red-600">{error}</div>}

        <div className="flex items-center gap-3 mt-6">
          <button type="button" onClick={() => navigate("/kyc-step2")} className="px-4 py-2 rounded border bg-white">Back</button>
          <button type="submit" disabled={uploading} className="ml-auto px-6 py-3 bg-blue-700 text-white rounded">
            {uploading ? "Uploading..." : "Upload & Next"} <RiArrowRightLine />
          </button>
        </div>
      </motion.form>
    </div>
  );
}

/* small file input component */
function FileInput({ label, name, file, onChange }) {
  return (
    <label className="block">
      <div className="text-sm font-medium text-slate-700 mb-2">{label}</div>
      <div
        className="flex items-center gap-4 border border-dashed rounded p-4 cursor-pointer hover:border-blue-400"
        onClick={() => document.getElementById(`fi_${name}`)?.click()}
      >
        <input id={`fi_${name}`} type="file" accept=".jpg,.jpeg,.png,.pdf" onChange={onChange} className="hidden" />
        <div className="text-slate-500 flex items-center gap-2">
          <RiUploadCloudLine className="text-2xl text-blue-500" />
          <div>
            {file ? (
              <>
                <div className="font-medium text-slate-800">{file.name}</div>
                <div className="text-xs text-slate-400">{Math.round(file.size / 1024)} KB</div>
              </>
            ) : (
              <div className="text-sm">Click or drop file here</div>
            )}
          </div>
        </div>
      </div>
    </label>
  );
}
