// src/pages/kyc-step2.jsx
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowRightLine, RiArrowLeftLine } from "react-icons/ri";

const STORAGE = "dk_kyc_step2";

export default function KYCStep2() {
  const navigate = useNavigate();
  const [aadhaar, setAadhaar] = useState("");
  const [pan, setPan] = useState("");
  const [passport, setPassport] = useState("");
  const [passportExpiry, setPassportExpiry] = useState("");
  const [error, setError] = useState("");
  const [progress] = useState(20); // step 2 of 5 => 20%

  useEffect(() => {
    try {
      const saved = JSON.parse(localStorage.getItem(STORAGE) || "{}");
      setAadhaar(saved.aadhaar || "");
      setPan(saved.pan || "");
      setPassport(saved.passport || "");
      setPassportExpiry(saved.passportExpiry || "");
    } catch {}
  }, []);

  useEffect(() => {
    try {
      localStorage.setItem(STORAGE, JSON.stringify({ aadhaar, pan, passport, passportExpiry }));
    } catch {}
  }, [aadhaar, pan, passport, passportExpiry]);

  function maskNumber(n) {
    if (!n) return "—";
    if (n.length <= 4) return n;
    return "********" + n.slice(-4);
  }

  const validateAndNext = (e) => {
    e?.preventDefault();
    setError("");

    // Aadhaar basic validation (12 digits)
    if (!/^\d{12}$/.test(aadhaar)) {
      setError("Please enter a valid 12-digit Aadhaar number.");
      return;
    }
    if (!/^[A-Z]{5}[0-9]{4}[A-Z]{1}$/i.test(pan)) {
      setError("Please enter a valid PAN (format: AAAAA9999A).");
      return;
    }

    // If passport provided, check expiry >= 6 months
    if (passport) {
      if (!passportExpiry) {
        setError("Please provide passport expiry date.");
        return;
      }
      const exp = new Date(passportExpiry);
      const min = new Date();
      min.setMonth(min.getMonth() + 6);
      if (exp < min) {
        setError("Passport must be valid for at least 6 months from now.");
        return;
      }
    }

    // If everything ok, save and go to upload step
    navigate("/kyc-step3");
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-gradient-to-br from-blue-50 to-white">
      <motion.form
        onSubmit={validateAndNext}
        initial={{ opacity: 0, y: 8 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow"
      >
        <div className="flex justify-between items-start mb-6">
          <div>
            <div className="text-sm text-slate-500">Step 2 of 5</div>
            <h2 className="text-2xl font-bold text-slate-800">Enter your document details</h2>
            <p className="text-sm text-slate-500 mt-1">
              Provide Aadhaar and PAN (both mandatory for digital accounts). Passport is optional but must be valid ≥ 6 months.
            </p>
          </div>
          <div className="text-sm text-slate-400">Progress: {progress}%</div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <label className="block">
            <div className="text-sm font-medium text-slate-700">Aadhaar number *</div>
            <input
              value={aadhaar}
              onChange={(e) => setAadhaar(e.target.value.replace(/\D/g, "").slice(0, 12))}
              placeholder="123412341234"
              className="mt-2 w-full border rounded px-3 py-2"
            />
            <div className="text-xs text-slate-400 mt-1">Will be masked in UI: {maskNumber(aadhaar)}</div>
          </label>

          <label className="block">
            <div className="text-sm font-medium text-slate-700">PAN number *</div>
            <input
              value={pan}
              onChange={(e) => setPan(e.target.value.toUpperCase().slice(0, 10))}
              placeholder="ABCDE1234F"
              className="mt-2 w-full border rounded px-3 py-2"
            />
          </label>

          <label className="block">
            <div className="text-sm font-medium text-slate-700">Passport number (optional)</div>
            <input
              value={passport}
              onChange={(e) => setPassport(e.target.value.toUpperCase().slice(0, 20))}
              placeholder="Passport #"
              className="mt-2 w-full border rounded px-3 py-2"
            />
          </label>

          <label className="block">
            <div className="text-sm font-medium text-slate-700">Passport expiry (if passport provided)</div>
            <input
              type="date"
              value={passportExpiry}
              onChange={(e) => setPassportExpiry(e.target.value)}
              className="mt-2 w-full border rounded px-3 py-2"
            />
          </label>
        </div>

        {error && <div className="text-sm text-red-600 mt-4">{error}</div>}

        <div className="flex items-center gap-3 mt-6">
          <button
            type="button"
            onClick={() => navigate("/kyc-step1")}
            className="px-4 py-2 rounded border bg-white"
          >
            <RiArrowLeftLine /> Back
          </button>

          <button type="submit" className="ml-auto px-6 py-3 bg-blue-700 text-white rounded">
            Next <RiArrowRightLine />
          </button>
        </div>
      </motion.form>
    </div>
  );
}
