import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiArrowRightLine, RiArrowLeftLine } from "react-icons/ri";
import { motion } from "framer-motion";

const STORAGE_KEY = "dk_kyc_step2_docNumber";

export default function KYCStep2() {
  const [docNumber, setDocNumber] = useState(() => {
    try {
      return localStorage.getItem(STORAGE_KEY) || "";
    } catch {
      return "";
    }
  });

  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    try {
      localStorage.setItem(STORAGE_KEY, docNumber);
    } catch {}
  }, [docNumber]);

  const handleNext = async (e) => {
    if (e) e.preventDefault();
    if (!docNumber.trim()) return alert("Please enter your document number");

    setSaving(true);
    await new Promise((r) => setTimeout(r, 250));
    setSaving(false);

    navigate("/kyc-step3");
  };

  const handleBack = () => {
    navigate("/kyc-step1");
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") handleNext(e);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form
        onSubmit={handleNext}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-2xl border border-gray-100"
        aria-labelledby="kyc-step2-title"
      >
        <div className="flex items-center justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">
              Step <span className="font-semibold">2</span> of{" "}
              <span className="font-semibold">5</span>
            </div>
            <h2 id="kyc-step2-title" className="text-2xl font-extrabold text-slate-800">
              Enter document details
            </h2>
            <p className="mt-1 text-sm text-slate-500">
              We will match this with your uploaded document in the next steps.
            </p>
          </div>

          <div className="w-40">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden">
              <div
                className="h-2 rounded-full bg-blue-600"
                style={{ width: "40%" }}
              ></div>
            </div>
            <div className="text-xs text-slate-400 mt-2 text-right">
              Progress: 40%
            </div>
          </div>
        </div>

        <label
          htmlFor="docNumber"
          className="block text-sm font-medium text-slate-700 mb-2"
        >
          Document number
        </label>

        <input
          id="docNumber"
          type="text"
          placeholder="Enter document number"
          value={docNumber}
          onChange={(e) => setDocNumber(e.target.value)}
          onKeyDown={handleKeyDown}
          className="w-full border border-slate-200 rounded-lg p-3 mb-6 focus:ring-2 focus:ring-blue-200 focus:outline-none transition"
          aria-required="true"
        />

        <div className="flex gap-3">
          <button
            type="button"
            onClick={handleBack}
            className="flex items-center gap-2 px-4 py-2 rounded-lg border border-slate-200 text-sm bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-slate-200"
          >
            <RiArrowLeftLine /> Back
          </button>

          <button
            type="submit"
            disabled={saving}
            className="ml-auto inline-flex items-center gap-2 px-5 py-3 rounded-lg bg-blue-700 text-white font-medium shadow hover:opacity-95 focus:outline-none focus:ring-2 focus:ring-blue-300"
          >
            {saving ? "Saving..." : "Next"} <RiArrowRightLine />
          </button>
        </div>
      </motion.form>
    </div>
  );
}
