import React, { useState, useContext } from "react";
import API, { setToken } from "../services/api";
import { useNavigate, useLocation } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { motion } from "framer-motion";
import { RiEyeLine, RiEyeOffLine, RiLoginBoxLine } from "react-icons/ri";

export default function Login() {
  const [kycId, setKycId] = useState("");
  const [password, setPassword] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errMsg, setErrMsg] = useState("");

  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useContext(AuthContext);

  // Redirect to saved page after login (e.g., user came from /kyc-step3)
  const redirectTo = location.state?.from?.pathname || "/dashboard";

  const handleLogin = async (e) => {
    e?.preventDefault();
    setErrMsg("");

    if (!kycId.trim() || !password.trim()) {
      return setErrMsg("Please enter both KYC ID and password.");
    }

    try {
      setLoading(true);
      const res = await API.post("/auth/login", { kycId, password });

      // Save JWT token
      setToken(res.data.token);

      // Update auth state
      login(res.data.token, res.data.role);

      navigate(redirectTo, { replace: true });
    } catch (err) {
      setErrMsg(err.response?.data?.message || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form
        onSubmit={handleLogin}
        initial={{ opacity: 0, y: 18 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.45 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-xl border border-gray-100"
      >
        <h1 className="text-3xl font-extrabold text-center text-slate-800 mb-2">
          Welcome Back
        </h1>
        <p className="text-center text-sm text-slate-500 mb-6">
          Sign in using your KYC credentials
        </p>

        {errMsg && (
          <div className="mb-4 p-3 rounded-lg bg-red-50 text-red-600 text-sm border border-red-200">
            {errMsg}
          </div>
        )}

        {/* KYC ID */}
        <label className="block mb-3">
          <span className="text-sm text-slate-600">KYC ID</span>
          <input
            type="text"
            placeholder="Enter your KYC ID"
            value={kycId}
            onChange={(e) => setKycId(e.target.value)}
            className="mt-1 w-full border border-slate-200 rounded-lg p-3 focus:ring-2 focus:ring-blue-200 focus:outline-none"
          />
        </label>

        {/* Password */}
        <label className="block mb-4">
          <span className="text-sm text-slate-600">Password</span>
          <div className="mt-1 relative flex items-center">
            <input
              type={showPass ? "text" : "password"}
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border border-slate-200 rounded-lg p-3 pr-10 focus:ring-2 focus:ring-blue-200 focus:outline-none"
            />
            <button
              type="button"
              className="absolute right-3 text-slate-500 hover:text-slate-700"
              onClick={() => setShowPass((prev) => !prev)}
            >
              {showPass ? <RiEyeOffLine size={20} /> : <RiEyeLine size={20} />}
            </button>
          </div>
        </label>

        {/* Login Button */}
        <button
          type="submit"
          disabled={loading}
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-lg shadow-md transition flex items-center justify-center gap-2"
        >
          {loading ? (
            <span className="animate-pulse">Authenticating...</span>
          ) : (
            <>
              Login <RiLoginBoxLine size={20} />
            </>
          )}
        </button>

        {/* Footer */}
        <p className="text-center text-xs text-slate-400 mt-4">
          By logging in, you agree to our Terms & Privacy Policy.
        </p>
      </motion.form>
    </div>
  );
}
