// src/pages/kyc-step1.jsx
import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiCameraLine, RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";
import ProgressBar from "../components/ProgressBar";

const STORAGE_KEY_PERSONAL = "dk_kyc_personal";

export default function KYCStep1() {
  const navigate = useNavigate();
  const fileRef = useRef(null);

  const [form, setForm] = useState({
    fullName: "",
    dob: "",
    fatherName: "",
    address: "",
    city: "",
    state: "",
    pincode: "",
  });
  const [photo, setPhoto] = useState(null); // File
  const [error, setError] = useState("");

  useEffect(() => {
    // load saved
    try {
      const saved = JSON.parse(localStorage.getItem(STORAGE_KEY_PERSONAL) || "{}");
      if (saved) {
        setForm(prev => ({ ...prev, ...saved.form }));
        if (saved.photoName) setPhoto({ name: saved.photoName, preview: saved.photoPreview || null });
      }
    } catch {}
  }, []);

  const handleChange = (field, val) => setForm(prev => ({ ...prev, [field]: val }));

  const onChoosePhoto = (file) => {
    setError("");
    if (!file) return setPhoto(null);
    const allowed = ["image/jpeg", "image/png"];
    if (!allowed.includes(file.type)) return setError("Only JPG/PNG passport photos allowed.");
    if (file.size > 2 * 1024 * 1024) return setError("Photo too large. Max 2 MB.");
    const reader = new FileReader();
    reader.onload = (e) => {
      setPhoto({ file, name: file.name, preview: e.target.result });
    };
    reader.readAsDataURL(file);
  };

  const handlePhotoInput = (e) => {
    const f = e.target.files?.[0];
    onChoosePhoto(f);
  };

  const handleNext = (e) => {
    e?.preventDefault();
    setError("");

    // basic validation
    if (!form.fullName || !form.dob || !form.address) {
      setError("Please provide full name, DOB and address.");
      return;
    }
    if (!photo) {
      setError("Please upload a passport-size photo.");
      return;
    }

    // save to localStorage
    try {
      localStorage.setItem(
        STORAGE_KEY_PERSONAL,
        JSON.stringify({
          form,
          photoName: photo.name,
          photoPreview: photo.preview || null,
        })
      );
    } catch {}

    // go to next
    navigate("/kyc-step2");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form
        onSubmit={handleNext}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.4 }}
        className="w-full max-w-2xl bg-white p-8 rounded-2xl shadow-2xl border border-gray-100"
      >
        <div className="mb-4">
          <ProgressBar step={1} total={5} />
        </div>

        <h2 className="text-2xl font-extrabold mb-4">Personal details</h2>
        <p className="text-sm text-slate-500 mb-6">Fill in your personal details and upload a passport-size photo.</p>

        <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
          <input value={form.fullName} onChange={e=>handleChange("fullName", e.target.value)} placeholder="Full Name" className="border p-2 rounded" />
          <input type="date" value={form.dob} onChange={e=>handleChange("dob", e.target.value)} className="border p-2 rounded" />
          <input value={form.fatherName} onChange={e=>handleChange("fatherName", e.target.value)} placeholder="Father's Name" className="border p-2 rounded" />
          <input value={form.pincode} onChange={e=>handleChange("pincode", e.target.value)} placeholder="Pincode" className="border p-2 rounded" />
          <input value={form.city} onChange={e=>handleChange("city", e.target.value)} placeholder="City" className="border p-2 rounded col-span-1 sm:col-span-2" />
          <input value={form.state} onChange={e=>handleChange("state", e.target.value)} placeholder="State" className="border p-2 rounded col-span-1 sm:col-span-2" />
        </div>

        <label className="mt-4 block text-sm text-slate-600">Address</label>
        <textarea value={form.address} onChange={e=>handleChange("address", e.target.value)} placeholder="Full Address" className="w-full border p-2 rounded h-24" />

        <div className="mt-4 grid grid-cols-1 sm:grid-cols-2 gap-4 items-center">
          <div>
            <div className="text-sm text-slate-600 mb-2">Passport-size photo</div>
            <div onClick={()=>fileRef.current?.click()} className="flex items-center gap-3 cursor-pointer border-2 border-dashed border-slate-200 rounded p-3">
              <div className="bg-blue-50 p-2 rounded"><RiCameraLine className="text-blue-600" size={20} /></div>
              <div>
                <div className="text-sm font-medium">{photo ? photo.name : "Upload passport photo (JPG/PNG, max 2MB)"}</div>
                <div className="text-xs text-slate-400">{photo && "Click to replace"}</div>
              </div>
            </div>
            <input ref={fileRef} type="file" accept="image/*" className="hidden" onChange={handlePhotoInput} />
            {photo?.preview && <img src={photo.preview} alt="passport" className="mt-3 w-24 h-28 object-cover rounded shadow-sm" />}
          </div>

          <div className="self-end flex justify-end">
            <button type="submit" className="ml-auto inline-flex items-center gap-2 px-5 py-3 rounded-lg bg-blue-700 text-white font-medium shadow">
              Next <RiArrowRightLine />
            </button>
          </div>
        </div>

        {error && <div className="text-red-600 mt-4">{error}</div>}
      </motion.form>
    </div>
  );
}
