import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowLeftLine, RiArrowRightLine } from "react-icons/ri";

export default function KYCStep2() {
  const navigate = useNavigate();

  const [docType, setDocType] = useState("Aadhaar Card");
  const [docNumber, setDocNumber] = useState("");

  const handleNext = () => {
    if (!docNumber.trim()) {
      return alert("Please enter your document number.");
    }

    localStorage.setItem(
      "kycDocument",
      JSON.stringify({
        docType,
        docNumber,
      })
    );

    navigate("/kyc-step3");
  };

  const handleBack = () => navigate("/kyc-step1");

  return (
    <div className="min-h-screen flex justify-center items-center bg-gradient-to-br from-blue-50 to-white p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl border border-gray-200"
      >
        <h2 className="text-blue-700 font-bold text-xl mb-2">Step 2 of 5</h2>
        <h1 className="text-3xl font-extrabold mb-6 text-gray-800">
          Document Verification
        </h1>

        {/* Document Type Dropdown */}
        <label className="block text-gray-700 font-medium mb-1">
          Address Proof 
        </label>
        <select
          className="w-full border-gray-300 border rounded-xl p-3 mb-6 focus:ring-2 focus:ring-blue-500 outline-none"
          value={docType}
          onChange={(e) => setDocType(e.target.value)}
        >
          <option>Aadhaar Card</option>
          <option>PAN Card</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>

        {/* Document Number Input */}
        <label className="block text-gray-700 font-medium mb-1">
          Enter Document Number
        </label>
        <input
          type="text"
          placeholder="Document Number"
          className="w-full border-gray-300 border rounded-xl p-3 mb-6 focus:ring-2 focus:ring-blue-500 outline-none"
          value={docNumber}
          onChange={(e) => setDocNumber(e.target.value)}
        />

        {/* Document Type Dropdown */}
        <label className="block text-gray-700 font-medium mb-1">
          Select Document Type
        </label>
        <select
          className="w-full border-gray-300 border rounded-xl p-3 mb-6 focus:ring-2 focus:ring-blue-500 outline-none"
          value={docType}
          onChange={(e) => setDocType(e.target.value)}
        >
          <option>Aadhaar Card</option>
          <option>PAN Card</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>

        {/* Document Number Input */}
        <label className="block text-gray-700 font-medium mb-1">
          Enter Document Number
        </label>
        <input
          type="text"
          placeholder="Document Number"
          className="w-full border-gray-300 border rounded-xl p-3 mb-6 focus:ring-2 focus:ring-blue-500 outline-none"
          value={docNumber}
          onChange={(e) => setDocNumber(e.target.value)}
        />

        {/* Navigation Buttons */}
        <div className="flex justify-between">
          <button
            onClick={handleBack}
            className="px-5 py-3 border border-gray-300 rounded-xl flex items-center gap-2 hover:bg-gray-100 transition"
          >
            <RiArrowLeftLine size={20} /> Back
          </button>

          <button
            onClick={handleNext}
            className="px-6 py-3 bg-blue-700 hover:bg-blue-800 text-white rounded-xl flex items-center gap-2 font-semibold shadow-md"
          >
            Next <RiArrowRightLine size={22} />
          </button>
        </div>
      </motion.div>
    </div>
  );
}
