// src/pages/kyc-step3.jsx
import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiUploadCloudLine, RiArrowRightLine, RiArrowLeftLine } from "react-icons/ri";
import { motion } from "framer-motion";
import ProgressBar from "../components/ProgressBar";
import API from "../services/api";

const STORAGE_KEY_DOCINFO = "dk_kyc_docinfo";

export default function KYCStep3() {
  const navigate = useNavigate();
  const fileRef = useRef(null);

  const [docType, setDocType] = useState("Aadhaar");
  const [file, setFile] = useState(null);
  const [error, setError] = useState("");
  const [uploading, setUploading] = useState(false);
  const [progress, setProgress] = useState(0);

  useEffect(()=> {
    try {
      const saved = JSON.parse(localStorage.getItem(STORAGE_KEY_DOCINFO) || "{}");
      if (saved) {
        setDocType(saved.docType || "Aadhaar");
      }
    } catch {}
  }, []);

  const onChooseFile = (f) => {
    setError("");
    if (!f) return setFile(null);
    const allowed = ["image/jpeg","image/png","application/pdf"];
    if (!allowed.includes(f.type)) return setError("Only JPG/PNG/PDF allowed.");
    if (f.size > 5*1024*1024) return setError("File too large. Max 5MB.");
    setFile(f);
  };

  const handleFileChange = (e) => {
    const f = e.target.files?.[0];
    onChooseFile(f);
  };

  const handleBack = ()=> navigate("/kyc-step2");

  const handleUpload = async (e) => {
    e?.preventDefault();
    setError("");
    if (!file) { setError("Please select a file to upload."); return; }

    // save docinfo locally too
    try { localStorage.setItem(STORAGE_KEY_DOCINFO, JSON.stringify({ docType, docNumber: localStorage.getItem("dk_kyc_docinfo") ? JSON.parse(localStorage.getItem("dk_kyc_docinfo")).docNumber : "" })); } catch {}

    const formData = new FormData();
    formData.append("document", file);
    formData.append("type", docType);

    try {
      setUploading(true);
      setProgress(0);

      const res = await API.post("/kyc/upload", formData, {
        headers: { "Content-Type":"multipart/form-data" },
        onUploadProgress: (evt) => {
          if (evt.total) setProgress(Math.round((evt.loaded * 100) / evt.total));
        }
      });

      // persist that upload succeeded (so review can show)
      try {
        const prev = JSON.parse(localStorage.getItem("dk_kyc_docinfo") || "{}");
        localStorage.setItem("dk_kyc_docinfo", JSON.stringify({ ...prev, uploadedFileName: file.name }));
      } catch {}

      navigate("/kyc-step4");
    } catch (err) {
      setError(err?.response?.data?.message || "Upload failed.");
    } finally {
      setUploading(false);
      setProgress(0);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form onSubmit={handleUpload} initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4 }} className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-2xl border border-gray-100">
        <div className="mb-4"><ProgressBar step={3} total={5} /></div>

        <h2 className="text-2xl font-extrabold mb-4">Upload Document</h2>
        <p className="text-sm text-slate-500 mb-6">Upload a clear photo or PDF of the selected identity document. Max 5 MB.</p>

        <label className="block text-sm font-medium text-slate-700 mb-2">Document type</label>
        <select value={docType} onChange={e=>setDocType(e.target.value)} className="w-full border border-slate-200 rounded-lg px-4 py-3 mb-4">
          <option>Aadhaar</option>
          <option>PAN</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>

        <div onClick={()=>fileRef.current?.click()} className="mb-4">
          <div className="flex items-center justify-center w-full h-36 border-2 border-dashed rounded-xl cursor-pointer hover:border-blue-500 transition bg-white">
            <input ref={fileRef} type="file" accept=".jpg,.jpeg,.png,.pdf" className="hidden" onChange={handleFileChange} />
            <div className="text-center text-slate-500">
              <div className="flex items-center justify-center gap-2">
                <RiUploadCloudLine className="text-3xl text-blue-500" />
                <div className="text-sm font-medium">{file ? "File selected" : "Click or drag file here to upload"}</div>
              </div>
              <div className="mt-2 text-xs">{file ? (<><div className="font-medium text-slate-700">{file.name}</div><div className="text-slate-400">{(file.size/1024).toFixed(1)} KB</div></>) : "Accepted: JPG, PNG, PDF · Max 5 MB"}</div>
            </div>
          </div>
        </div>

        {error && <div className="text-red-600 mb-3">{error}</div>}

        {uploading && (
          <div className="mb-3">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: `${progress}%` }} />
            </div>
            <div className="text-xs text-slate-400 mt-2 text-right">{progress}%</div>
          </div>
        )}

        <div className="flex gap-3">
          <button type="button" onClick={handleBack} className="flex items-center gap-2 px-4 py-2 rounded-lg border border-slate-200 bg-white"> <RiArrowLeftLine /> Back</button>
          <button type="submit" disabled={uploading} className="ml-auto inline-flex items-center gap-2 px-5 py-3 rounded-lg bg-blue-700 text-white font-medium shadow"> {uploading ? `Uploading ${progress}%` : "Upload & Next"} <RiArrowRightLine /></button>
        </div>
      </motion.form>
    </div>
  );
}
