import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiArrowRightLine } from "react-icons/ri";

export default function KYCStep2() {
  const [docNumber, setDocNumber] = useState("");
  const navigate = useNavigate();

  const handleNext = () => {
    if (!docNumber) return alert("Please enter your document number");
    navigate("/kyc-step3");
  };

  return (
    <div className="h-full w-full flex justify-center items-center">
      <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-lg border border-gray-200">
        <h1 className="text-3xl font-bold mb-6 text-gray-800 text-center">Step 2: Enter Document Details</h1>

        <input
          type="text"
          placeholder="Enter Document Number"
          value={docNumber}
          onChange={e => setDocNumber(e.target.value)}
          className="w-full border-gray-300 border rounded-lg p-3 mb-6 focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />

        <button
          onClick={handleNext}
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-lg flex justify-center items-center gap-2 transition duration-200"
        >
          Next <RiArrowRightLine size={20} />
        </button>
      </div>
    </div>
  );
}
