import { useState } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";

export default function KYCStep3() {
  const [file, setFile] = useState(null);
  const [docType, setDocType] = useState("Aadhaar");
  const navigate = useNavigate();

  const handleUpload = async () => {
    if(!file) return alert("Select a file");
    const formData = new FormData();
    formData.append("document", file);
    formData.append("type", docType);

    try {
      const res = await API.post("/kyc/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      alert(`Upload successful! Attempts left: ${res.data.attemptsLeft}`);
      navigate("/kyc-step4");
    } catch (err) {
      alert(err.response?.data?.message || "Upload failed");
    }
  };

  return (
    <div className="h-screen flex justify-center items-center bg-gray-50">
      <div className="w-96 bg-white p-6 rounded shadow">
        <h1 className="text-2xl font-bold mb-4">KYC Step 3 â€“ Upload Document</h1>
        <select className="w-full border p-2 mb-2 rounded" onChange={e=>setDocType(e.target.value)}>
          <option>Aadhaar</option>
          <option>PAN</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>
        <input type="file" className="w-full mb-4" onChange={e=>setFile(e.target.files[0])} />
        <button onClick={handleUpload} className="w-full bg-blue-600 text-white p-2 rounded">Upload</button>
      </div>
    </div>
  );
}
