import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiCheckLine, RiArrowRightLine, RiArrowLeftLine, RiEditLine } from "react-icons/ri";
import { motion } from "framer-motion";

const KEY_STEP1 = "dk_kyc_step1_docType";
const KEY_STEP2 = "dk_kyc_step2_docNumber";
const KEY_STEP3 = "dk_kyc_step3_docType";

export default function KYCStep4() {
  const navigate = useNavigate();
  const [docType, setDocType] = useState("");
  const [docNumber, setDocNumber] = useState("");
  const [uploadedDocType, setUploadedDocType] = useState("");
  const [confirming, setConfirming] = useState(false);

  useEffect(() => {
    try {
      const s1 = localStorage.getItem(KEY_STEP1) || "";
      const s2 = localStorage.getItem(KEY_STEP2) || "";
      const s3 = localStorage.getItem(KEY_STEP3) || "";
      setDocType(s1 || s3 || "—");
      setUploadedDocType(s3 || s1 || "—");
      setDocNumber(s2 || "—");
    } catch (e) {
      setDocType("");
      setDocNumber("");
      setUploadedDocType("");
    }
  }, []);

  const handleBack = () => navigate("/kyc-step3");
  const handleEditDocType = () => navigate("/kyc-step1");
  const handleEditDocNumber = () => navigate("/kyc-step2");

  const handleConfirm = async () => {
    // quick client-side guard
    if (!docNumber || docNumber === "—") {
      alert("Document number missing — please complete Step 2.");
      return navigate("/kyc-step2");
    }

    setConfirming(true);
    try {
      // simulate server-side verification delay
      await new Promise((r) => setTimeout(r, 700));
      // in a real app: call API to confirm review data here
      navigate("/kyc-step5");
    } catch (err) {
      // show a simple error and allow retry
      alert("Unable to confirm at the moment. Please try again.");
    } finally {
      setConfirming(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.div
        initial={{ opacity: 0, y: 12 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.45 }}
        className="w-full max-w-lg bg-white p-8 rounded-2xl shadow-2xl border border-gray-100"
        aria-labelledby="kyc-step4-title"
      >
        <div className="flex items-center justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">
              Step <span className="font-semibold">4</span> of <span className="font-semibold">5</span>
            </div>
            <h2 id="kyc-step4-title" className="text-2xl font-extrabold text-slate-800">
              Review & confirm your details
            </h2>
            <p className="mt-1 text-sm text-slate-500">
              Please verify the details below before submission. You can edit any item if something looks wrong.
            </p>
          </div>

          <div className="w-40">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: "80%" }} />
            </div>
            <div className="text-xs text-slate-400 mt-2 text-right">Progress: 80%</div>
          </div>
        </div>

        <dl className="grid grid-cols-1 gap-4 mb-6">
          <div className="flex items-start justify-between gap-4 p-4 rounded-lg border border-slate-100 bg-slate-50">
            <div>
              <dt className="text-xs text-slate-500">Selected document</dt>
              <dd className="mt-1 font-medium text-slate-800 flex items-center gap-2">
                <RiCheckLine className="text-green-500" /> {docType || "—"}
              </dd>
              <p className="mt-1 text-xs text-slate-400">This is the document type you chose in Step 1.</p>
            </div>
            <div className="flex flex-col items-end gap-2">
              <button
                onClick={handleEditDocType}
                className="inline-flex items-center gap-2 px-3 py-1.5 rounded-md border text-sm bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-slate-200"
                aria-label="Edit selected document"
              >
                <RiEditLine /> Edit
              </button>
            </div>
          </div>

          <div className="flex items-start justify-between gap-4 p-4 rounded-lg border border-slate-100 bg-slate-50">
            <div>
              <dt className="text-xs text-slate-500">Document number</dt>
              <dd className="mt-1 font-medium text-slate-800 flex items-center gap-2">
                <RiCheckLine className="text-green-500" /> {docNumber || "—"}
              </dd>
              <p className="mt-1 text-xs text-slate-400">Masked in UI for privacy. Full value is submitted to verification service.</p>
            </div>
            <div className="flex flex-col items-end gap-2">
              <button
                onClick={handleEditDocNumber}
                className="inline-flex items-center gap-2 px-3 py-1.5 rounded-md border text-sm bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-slate-200"
                aria-label="Edit document number"
              >
                <RiEditLine /> Edit
              </button>
            </div>
          </div>

          <div className="flex items-start justify-between gap-4 p-4 rounded-lg border border-slate-100 bg-slate-50">
            <div>
              <dt className="text-xs text-slate-500">Uploaded file</dt>
              <dd className="mt-1 font-medium text-slate-800 flex items-center gap-2">
                <RiCheckLine className="text-green-500" /> {uploadedDocType || "—"}
              </dd>
              <p className="mt-1 text-xs text-slate-400">The file you uploaded in Step 3 will be used to verify the document.</p>
            </div>
            <div className="flex flex-col items-end gap-2">
              <button
                type="button"
                onClick={() => navigate("/kyc-step3")}
                className="inline-flex items-center gap-2 px-3 py-1.5 rounded-md border text-sm bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-slate-200"
                aria-label="Change uploaded file"
              >
                Change
              </button>
            </div>
          </div>
        </dl>

        <div className="flex gap-3">
          <button
            type="button"
            onClick={handleBack}
            className="flex items-center gap-2 px-4 py-2 rounded-lg border border-slate-200 text-sm bg-white hover:shadow-sm focus:outline-none focus:ring-2 focus:ring-slate-200"
          >
            <RiArrowLeftLine /> Back
          </button>

          <button
            type="button"
            onClick={handleConfirm}
            disabled={confirming}
            className="ml-auto inline-flex items-center gap-2 px-5 py-3 rounded-lg bg-green-600 text-white font-medium shadow hover:opacity-95 focus:outline-none focus:ring-2 focus:ring-green-300"
            aria-disabled={confirming}
          >
            {confirming ? "Confirming..." : "Confirm"} <RiArrowRightLine />
          </button>
        </div>

        <p className="mt-4 text-xs text-slate-400">
          By confirming you agree to the verification process. All data is transmitted securely and used only for KYC verification.
        </p>
      </motion.div>
    </div>
  );
}
