import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import Landing from "./pages/index";
import Login from "./pages/login";
import Register from "./pages/register";
import Dashboard from "./pages/dashboard";

import Layout from "./components/Layout";

import KYCStep1 from "./pages/kyc-step1";
import KYCStep2 from "./pages/kyc-step2";
import KYCStep3 from "./pages/kyc-step3";
import KYCStep4 from "./pages/kyc-step4";
import KYCStep5 from "./pages/kyc-step5";

function App() {
  return (
    <Router>
      <Routes>

        {/* ❌ These pages SHOULD NOT use Layout */}
        <Route path="/" element={<Landing />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/dashboard" element={<Dashboard />} />

        {/* ✅ KYC steps SHOULD use Layout */}
        <Route element={<Layout />}>
          <Route path="/kyc-step1" element={<KYCStep1 />} />
          <Route path="/kyc-step2" element={<KYCStep2 />} />
          <Route path="/kyc-step3" element={<KYCStep3 />} />
          <Route path="/kyc-step4" element={<KYCStep4 />} />
          <Route path="/kyc-step5" element={<KYCStep5 />} />
        </Route>

      </Routes>
    </Router>
  );
}

export default App;
