// src/pages/dashboard.jsx
import React, { useEffect, useState } from "react";
import {
  RiBankLine,
  RiMoneyDollarCircleLine,
  RiFileListLine,
  RiShieldCheckLine,
  RiBankCardLine,
  RiHandCoinLine,
  RiTrainLine,
  RiShoppingCartLine,
  RiSearchLine,
  RiUser3Line,
  RiNotification3Line,
} from "react-icons/ri";
import API from "../services/api";

/**
 * Dashboard
 * - Reads `kycPersonal` and `kycCompleteResponse` from localStorage (populated by KYC flow).
 * - If not present, tries to GET /api/kyc/dashboard (requires auth token set in API).
 * - Displays name, kyc status, kyc id, email, mobile, masked account number, etc.
 */

function maskAccountNo(acc) {
  if (!acc) return "Not assigned";
  const s = String(acc).replace(/\s+/g, "");
  if (s.length <= 4) return "****";
  return `XXXX-XXXX-${s.slice(-4)}`;
}

export default function Dashboard() {
  const [personal, setPersonal] = useState(null);
  const [kycResp, setKycResp] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Try to load from localStorage first (fast)
    try {
      const p = JSON.parse(localStorage.getItem("kycPersonal") || "null");
      const k = JSON.parse(localStorage.getItem("kycCompleteResponse") || "null");
      if (p) setPersonal(p);
      if (k) setKycResp(k);
    } catch (e) {
      console.warn("Failed to parse localStorage KYC data", e);
    }

    // Then try backend for the authoritative data (if token present)
    (async () => {
      try {
        const res = await API.get("/kyc/dashboard");
        // server response expected to contain personal + kyc fields (adjust if your API differs)
        // Example response: { personal: {...}, kyc: {...}, accountNo: '...', kycId: '...' }
        const data = res.data || {};
        // prefer server data if present
        if (data.personal) {
          setPersonal(data.personal);
          try { localStorage.setItem("kycPersonal", JSON.stringify(data.personal)); } catch {}
        }
        if (data.kyc || data) {
          // handle API that returns KYCApplication object directly
          const kyc = data.kyc || data;
          setKycResp(kyc);
          try { localStorage.setItem("kycCompleteResponse", JSON.stringify(kyc)); } catch {}
        }
      } catch (err) {
        // backend call failed: ignore silently (user may still have local data)
        // console.warn("Could not fetch dashboard data", err);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // safe getters
  const fullName = personal?.fullName || personal?.name || "Customer";
  const email = personal?.email || kycResp?.email || "—";
  const mobile = personal?.mobile || kycResp?.mobile || "—";
  const kycStatus = kycResp?.status || (kycResp ? kycResp : "Submitted");
  const kycId = kycResp?.kycId || "N/A";
  const accountNo = kycResp?.accountNo || (kycResp && kycResp.accountNumber) || null;
  const balanceDisplay = kycResp?.balance ?? "—";

  return (
    <div className="min-h-screen bg-gray-50">
      {/* TOP NAVBAR */}
      <header className="w-full bg-[#0047B3] text-white shadow-md py-4 px-8 flex items-center justify-between">
        <div>
          <div className="text-sm text-blue-100">Welcome back</div>
          <div className="text-lg font-semibold tracking-wide">{fullName}</div>
        </div>

        <div className="flex items-center gap-6">
          <div className="relative">
            <input
              type="text"
              placeholder="Search banking services..."
              className="py-2 pl-3 pr-10 rounded-md text-black w-64"
            />
            <RiSearchLine size={20} className="absolute top-2.5 right-3 text-gray-600" />
          </div>

          <RiNotification3Line size={22} className="cursor-pointer" />
          <RiUser3Line size={22} className="cursor-pointer" />
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-10">
        {/* KYC + Personal Details */}
        <section className="mb-8">
          <div className="bg-white rounded-lg shadow-md p-6 border border-gray-200 grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h3 className="text-lg font-semibold mb-3">KYC Details</h3>
              <div className="text-sm text-gray-700 mb-2">
                <span className="font-medium">KYC Status:</span>{" "}
                <span className={`ml-2 ${kycStatus === "Approved" ? "text-green-600 font-semibold" : (kycStatus === "Rejected" ? "text-red-600 font-semibold" : "text-yellow-600 font-semibold")}`}>
                  {kycStatus}
                </span>
              </div>

              <div className="text-sm text-gray-700 mb-1"><span className="font-medium">Name:</span> <span className="ml-2">{fullName}</span></div>
              <div className="text-sm text-gray-700 mb-1"><span className="font-medium">Mobile:</span> <span className="ml-2">{mobile}</span></div>
            </div>

            <div>
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold mb-3">Account Info</h3>
                <div className="text-sm text-gray-500">KYC ID: <span className="font-medium ml-2">{kycId}</span></div>
              </div>

              <div className="text-sm text-gray-700 mb-1"><span className="font-medium">Email:</span> <span className="ml-2">{email}</span></div>
              <div className="text-sm text-gray-700"><span className="font-medium">Account No:</span> <span className="ml-2">{accountNo ? maskAccountNo(accountNo) : "XXXX-XXXX-XXXX"}</span></div>
            </div>
          </div>
        </section>

        {/* PRIMARY ACCOUNT */}
        <section className="bg-white rounded-lg shadow-md p-6 mb-10 border border-gray-200">
          <h4 className="text-gray-600 text-sm font-medium">Primary Account</h4>
          <div className="text-3xl font-bold mt-1 text-gray-900">₹ {balanceDisplay === "—" ? "—" : Number(balanceDisplay).toLocaleString()}</div>
          <p className="text-gray-500 text-sm">Available Balance</p>

          <div className="flex gap-4 mt-6">
            <button className="bg-[#0047B3] text-white px-6 py-2 rounded-md hover:bg-[#003a96] transition">
              Transfer
            </button>
            <button className="border border-gray-300 px-6 py-2 rounded-md hover:bg-gray-100 transition">
              Statement
            </button>
          </div>
        </section>

        {/* MAIN SERVICES GRID */}
        <section>
          <h3 className="text-lg font-semibold mb-4 text-gray-700">Banking Services</h3>

          <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-6">
            <ServiceCard icon={<RiBankLine />} title="Accounts" />
            <ServiceCard icon={<RiMoneyDollarCircleLine />} title="Deposits" />
            <ServiceCard icon={<RiHandCoinLine />} title="Loans" />
            <ServiceCard icon={<RiFileListLine />} title="Investments" />
            <ServiceCard icon={<RiShieldCheckLine />} title="Insurance" />
            <ServiceCard icon={<RiBankCardLine />} title="Cards" />
            <ServiceCard icon={<RiHandCoinLine />} title="Payments" />
            <ServiceCard icon={<RiMoneyDollarCircleLine />} title="UPI & QR" />
          </div>
        </section>
      </main>
    </div>
  );
}

/* SERVICE CARD */
function ServiceCard({ icon, title }) {
  return (
    <div className="bg-white border border-gray-200 shadow-sm hover:shadow-md transition rounded-lg p-6 flex flex-col items-center text-center cursor-pointer">
      <div className="text-[#0047B3] mb-3" style={{ fontSize: "28px" }}>
        {icon}
      </div>
      <div className="font-semibold text-gray-800">{title}</div>
    </div>
  );
}
