// src/pages/kyc-step5.jsx
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../services/api";
import { motion } from "framer-motion";
import { RiArrowLeftLine } from "react-icons/ri";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";

export default function KYCStep5() {
  const navigate = useNavigate();
  const [kycSummary, setKycSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [progress] = useState(80); // step 5 of 5

  useEffect(() => {
    // try to read preview data from server
    API.get("/kyc/dashboard")
      .then((res) => setKycSummary(res.data))
      .catch(() => setKycSummary(null));
  }, []);

  const handleSubmit = async () => {
    setError("");
    setSubmitting(true);
    try {
      const res = await API.post("/kyc/submit");
      setKycSummary(res.data);
      // mark completed in UI
      // auto-download confirmation PDF
      await downloadConfirmationPDF(res.data);
      // navigate to dashboard
      navigate("/dashboard");
    } catch (err) {
      setError(err?.response?.data?.message || err.message || "Submit failed");
    } finally {
      setSubmitting(false);
    }
  };

  async function downloadConfirmationPDF(data) {
    // construct a small HTML confirmation for PDF capture
    const container = document.createElement("div");
    container.style.width = "800px";
    container.style.padding = "24px";
    container.style.fontFamily = "Arial, Helvetica, sans-serif";
    container.innerHTML = confirmationHtml(data);

    // try to make PDF via html2canvas + jsPDF
    try {
      document.body.appendChild(container); // needs to be in DOM for html2canvas
      const canvas = await html2canvas(container, { scale: 2 });
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF({ orientation: "portrait", unit: "pt", format: "a4" });
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      const imgProps = pdf.getImageProperties(imgData);
      const imgHeight = (imgProps.height * pageWidth) / imgProps.width;
      pdf.addImage(imgData, "PNG", 0, 0, pageWidth, imgHeight);
      const blob = pdf.output("blob");
      triggerDownloadBlob(blob, `KYC-confirmation-${(data?.name || "user").replace(/\s+/g, "_")}.pdf`);
    } catch (err) {
      // fallback: download the HTML as a file (user can print -> save as PDF)
      const blob = new Blob([confirmationHtml(data)], { type: "text/html" });
      triggerDownloadBlob(blob, `KYC-confirmation-${(data?.name || "user").replace(/\s+/g, "_")}.html`);
    } finally {
      try { document.body.removeChild(container); } catch {}
    }
  }

  function triggerDownloadBlob(blob, filename) {
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename;
    // append, click, remove
    document.body.appendChild(a);
    a.click();
    a.remove();
    URL.revokeObjectURL(url);
  }

  function confirmationHtml(data) {
    const name = data?.name || "Customer";
    const kycId = data?.kycId || "N/A";
    const accountNo = data?.accountNo || "Not assigned";
    const status = data?.status || "Completed";
    const submittedAt = data?.submittedAt || new Date().toLocaleString();
    const documentSummary = data?.documentSummary || "Aadhaar / ********7654";

    return `
      <div style="font-family: Arial, Helvetica, sans-serif; color:#0f172a;">
        <div style="display:flex;align-items:center;gap:16px;">
          <img src="/assets/hdfc.png" style="height:36px" alt="HDFC" />
          <h2 style="margin:0;color:#0b4aa6">Digital KYC - Confirmation</h2>
        </div>
        <div style="margin-top:18px;padding:18px;background:#f1f8ff;border-radius:6px;">
          <h3 style="margin:0">Congratulations, ${name}!</h3>
          <p style="margin:6px 0 0 0">Your KYC submission is received and is under verification. Keep this confirmation for your records.</p>
        </div>

        <table style="width:100%;margin-top:18px;border-collapse:collapse">
          ${row("Applicant", name)}
          ${row("KYC ID", kycId)}
          ${row("Account No", accountNo)}
          ${row("Status", status)}
          ${row("Submitted at", submittedAt)}
          ${row("Document", documentSummary)}
        </table>

        <div style="margin-top:18px;font-size:12px;color:#475569">Please retain this confirmation. Document numbers are masked for privacy.</div>
      </div>
    `;
  }

  function row(label, value) {
    return `<tr style="border-bottom:1px dashed #e6eef8"><td style="padding:12px 0; width:30%; font-weight:600">${label}</td><td style="padding:12px 0">${value}</td></tr>`;
  }

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-blue-50">
      <motion.div className="w-full max-w-xl bg-white p-8 rounded-2xl shadow">
        <div className="flex justify-between items-start mb-6">
          <div>
            <div className="text-sm text-slate-500">Step 5 of 5</div>
            <h2 className="text-2xl font-bold">Review & submit</h2>
            <p className="text-sm text-slate-500 mt-1">Confirm details and submit for final verification. A confirmation PDF will be generated.</p>
          </div>
          <div className="text-sm text-slate-400">Progress: {progress}%</div>
        </div>

        <div className="mb-4">
          <h4 className="font-semibold">Preview</h4>
          <div className="mt-2 text-sm text-slate-600">
            {kycSummary ? (
              <>
                <div><strong>Name:</strong> {kycSummary.name}</div>
                <div><strong>Email:</strong> {kycSummary.email}</div>
                <div><strong>Mobile:</strong> {kycSummary.mobile}</div>
                <div><strong>Status:</strong> {kycSummary.status}</div>
              </>
            ) : (
              <div className="text-slate-400">No preview available â€” we will generate confirmation from server after submit.</div>
            )}
          </div>
        </div>

        {error && <div className="text-sm text-red-600 mb-3">{error}</div>}

        <div className="flex gap-3">
          <button type="button" onClick={() => navigate("/kyc-step4")} className="px-4 py-2 border rounded">
            <RiArrowLeftLine /> Back
          </button>

          <button onClick={handleSubmit} disabled={submitting} className="ml-auto px-6 py-3 bg-green-600 text-white rounded">
            {submitting ? "Submitting..." : "Confirm & Submit"}
          </button>
        </div>
      </motion.div>
    </div>
  );
}
