// src/pages/kyc-step5.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiDownloadCloudLine, RiCheckLine, RiArrowRightLine } from "react-icons/ri";
import HDFCLogoUrl from "../assets/hdfc.png"; // webpack URL
import jsPDF from "jspdf";

/* helpers */
const maskAccount = (acc) => {
  if (!acc) return "Not assigned";
  const s = String(acc).replace(/\s+/g, "");
  return s.length <= 4 ? "****" : `XXXX-XXXX-${s.slice(-4)}`;
};
const maskDoc = (d) => {
  if (!d) return "—";
  const s = String(d);
  if (s.length <= 4) return "****";
  return `${"*".repeat(Math.max(0, s.length - 4))}${s.slice(-4)}`;
};
const fmtDate = (iso) => {
  try { return new Date(iso).toLocaleString(); } catch { return new Date().toLocaleString(); }
};

/* convert an image URL to base64 dataURL */
async function urlToDataUrl(url) {
  try {
    const res = await fetch(url, { mode: "cors" });
    const blob = await res.blob();
    return await new Promise((resolve, reject) => {
      const fr = new FileReader();
      fr.onload = () => resolve(fr.result);
      fr.onerror = reject;
      fr.readAsDataURL(blob);
    });
  } catch (e) {
    console.warn("Failed to convert image to dataURL:", e);
    return null;
  }
}

export default function KYCStep5() {
  const navigate = useNavigate();
  const [personal, setPersonal] = useState(null);
  const [docData, setDocData] = useState(null);
  const [completeResp, setCompleteResp] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // read from localStorage (set earlier in your flow)
    try { setPersonal(JSON.parse(localStorage.getItem("kycPersonal") || "null")); } catch { setPersonal(null); }
    try { setDocData(JSON.parse(localStorage.getItem("kycDocument") || "null")); } catch { setDocData(null); }
    try { setCompleteResp(JSON.parse(localStorage.getItem("kycCompleteResponse") || "null")); } catch { setCompleteResp(null); }
  }, []);

  const generatePdfAndDownload = async () => {
    if (loading) return;
    setLoading(true);

    try {
      const applicant = personal?.fullName || personal?.name || "Customer";
      const kycId = completeResp?.kycId || "N/A";
      const accountNo = completeResp?.accountNo || null;
      const status = completeResp?.status || "Submitted";
      const submittedAt = completeResp?.submittedAt || new Date().toISOString();
      const docType = docData?.docType || "—";
      const docNumber = docData?.docNumber || "—";

      // convert logo to dataURL (works with local webpack asset or remote)
      const logoDataUrl = await urlToDataUrl(HDFCLogoUrl);

      // create jsPDF
      const pdf = new jsPDF({ unit: "mm", format: "a4", orientation: "p" });
      const pageW = pdf.internal.pageSize.getWidth();
      const margin = 14;
      let y = margin;

      // header: optional logo
      if (logoDataUrl) {
        const imgProps = pdf.getImageProperties(logoDataUrl);
        const logoHeight = 14; // mm
        const logoWidth = (imgProps.width / imgProps.height) * logoHeight;
        pdf.addImage(logoDataUrl, "PNG", margin, y, logoWidth, logoHeight);
      }

      // title
      pdf.setFontSize(18);
      pdf.setFont("helvetica", "bold");
      const titleX = margin + (logoDataUrl ? 25 : 0);
      pdf.text("KYC Confirmation", titleX, y + 10);
      y += 22;

      // subtitle
      pdf.setFontSize(10);
      pdf.setFont("helvetica", "normal");
      pdf.setTextColor(80, 90, 100);
      pdf.text("This is your KYC confirmation. Keep it for your records.", margin, y);
      y += 8;

      // a thin separator
      pdf.setDrawColor(230, 235, 240);
      pdf.setLineWidth(0.5);
      pdf.line(margin, y, pageW - margin, y);
      y += 8;

      // left column: details (we'll write key/value lines)
      const leftX = margin;
      const midX = pageW / 2 + 6;
      pdf.setFontSize(11);
      pdf.setTextColor(30, 40, 50);
      const addLine = (label, value, col = "left") => {
        const labelFont = { size: 10, style: "bold" };
        const valFont = { size: 11, style: "normal" };
        if (col === "left") {
          pdf.setFontSize(labelFont.size); pdf.setFont("helvetica", "bold");
          pdf.text(label + ":", leftX, y);
          pdf.setFontSize(valFont.size); pdf.setFont("helvetica", "normal");
          pdf.text(String(value), leftX + 38, y);
        } else {
          pdf.setFontSize(labelFont.size); pdf.setFont("helvetica", "bold");
          pdf.text(label + ":", midX, y);
          pdf.setFontSize(valFont.size); pdf.setFont("helvetica", "normal");
          pdf.text(String(value), midX + 34, y);
        }
        y += 8;
      };

      addLine("Applicant", applicant, "left");
      addLine("KYC ID", kycId, "left");
      addLine("Account No", accountNo ? maskAccount(accountNo) : "Not assigned", "left");
      addLine("Status", status, "left");
      addLine("Submitted at", fmtDate(submittedAt), "left");
      y += 2;

      // Document block on right column (if space reached, move down)
      y += 4;
      pdf.setDrawColor(230,235,240);
      pdf.setFillColor(246, 249, 252);
      pdf.roundedRect(margin, y, pageW - margin * 2, 30, 4, 4, "S");
      pdf.setFontSize(11);
      pdf.setTextColor(20, 40, 60);
      pdf.text(`Document: ${docType} (${maskDoc(docNumber)})`, margin + 6, y + 10);
      y += 40;

      // Important note
      pdf.setFontSize(9);
      pdf.setTextColor(110, 120, 130);
      pdf.text("Note: Full document numbers were submitted to the system securely and are not shown in full here for privacy.", margin, y);
      y += 12;

      // footer / generated by
      pdf.setFontSize(9);
      pdf.setTextColor(130, 140, 150);
      pdf.text(`Generated by Digital KYC System • ${fmtDate(new Date().toISOString())}`, margin, pdf.internal.pageSize.getHeight() - 12);

      // download
      const filename = `KYC-confirmation-${(applicant || "customer").replace(/\s+/g, "_")}.pdf`;
      pdf.save(filename);

      // done
    } catch (err) {
      console.error("PDF generate error:", err);
      alert("Could not generate PDF. See console.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-8">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-xl p-8 text-center">
        <div className="flex items-center justify-center mb-4">
          <div className="rounded-full bg-green-50 p-4 inline-flex items-center justify-center">
            <RiCheckLine className="text-green-600" size={36} />
          </div>
        </div>

        <h2 className="text-2xl font-extrabold mb-2">KYC Submitted</h2>
        <p className="text-gray-600 mb-6">Thanks {personal?.fullName || "Customer"} — your KYC is submitted.</p>

        <div className="flex gap-4 justify-center mb-4">
          <button
            type="button"
            onClick={generatePdfAndDownload}
            disabled={loading}
            className={`inline-flex items-center gap-2 px-5 py-3 bg-blue-700 text-white rounded-lg shadow ${loading ? "opacity-70 cursor-not-allowed" : ""}`}>
            <RiDownloadCloudLine /> {loading ? "Preparing PDF..." : "Download Confirmation"}
          </button>

          <button type="button"
            onClick={() => navigate("/dashboard")}
            className="inline-flex items-center gap-2 px-5 py-3 bg-green-600 text-white rounded-lg shadow">
            Go to Dashboard <RiArrowRightLine />
          </button>
        </div>

        <div className="text-sm text-gray-400">Profile {personal ? "available" : "not available"}</div>
      </div>
    </div>
  );
}
