// src/pages/login.jsx
import React, { useState, useContext } from "react";
import API from "../services/api";
import { useNavigate, useLocation } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { motion } from "framer-motion";
import { RiEyeLine, RiEyeOffLine, RiLoginBoxLine } from "react-icons/ri";

export default function Login() {
  const [kycId, setKycId] = useState("");
  const [password, setPassword] = useState("");
  const [showPass, setShowPass] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errMsg, setErrMsg] = useState("");

  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useContext(AuthContext);

  // If user was blocked from a protected route, it will be here:
  const redirectFrom = location.state?.from?.pathname || null;

  const handleLogin = async (e) => {
    e?.preventDefault();
    setErrMsg("");

    if (!kycId.trim() || !password.trim()) {
      setErrMsg("Please enter both KYC ID and password.");
      return;
    }

    try {
      setLoading(true);

      // your backend login - expect token, role, and (ideally) user object
      const res = await API.post("/auth/login", { kycId, password });

      const token = res?.data?.token;
      const role = res?.data?.role;
      let user = res?.data?.user || null;

      // If backend doesn't return user, fetch current user
      if (!user && token) {
        // ensure token used by axios (api.js interceptor reads localStorage)
        // but setToken also exists if you rely on it; we set localStorage directly below via login()
        try {
          const meRes = await API.get("/auth/me");
          user = meRes?.data || null;
        } catch (err) {
          // ignore - user stays null
        }
      }

      // Persist auth in context (login should save token/role/user to localStorage)
      login(token, role, user || {});

      // Decide where to go next:
      // 1) If user attempted to visit a protected page, go there
      // 2) Else if user's KYC is not complete, go to kyc-step1
      // 3) Else go to dashboard
      let nextRoute = "/dashboard";

      if (redirectFrom) {
        nextRoute = redirectFrom;
      } else if (user) {
        // Adjust this condition to match your backend's KYC status key/value
        // Common keys: user.kycStatus, user.isKycComplete, user.kyc_completed
        const kycStatus = (user.kycStatus || user.kyc_status || user.isKycComplete || user.kyc_completed);
        // If status fields use boolean
        if (kycStatus === false || kycStatus === "PENDING" || kycStatus === "INCOMPLETE" || kycStatus === "NOT_SUBMITTED" || kycStatus === undefined) {
          nextRoute = "/kyc-step1";
        } else if (typeof kycStatus === "string" && kycStatus.toLowerCase() === "verified") {
          nextRoute = "/dashboard";
        } else if (kycStatus === true) {
          nextRoute = "/dashboard";
        } else {
          // conservative default: send to KYC step 1 if unsure
          nextRoute = "/kyc-step1";
        }
      } else {
        // If no user info, be conservative and push to KYC first
        nextRoute = "/kyc-step1";
      }

      navigate(nextRoute, { replace: true });
    } catch (err) {
      setErrMsg(err.response?.data?.message || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.form
        onSubmit={handleLogin}
        initial={{ opacity: 0, y: 18 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.45 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-xl border border-gray-100"
      >
        <h1 className="text-3xl font-extrabold text-center text-slate-800 mb-2">Welcome Back</h1>
        <p className="text-center text-sm text-slate-500 mb-6">Sign in using your KYC credentials</p>

        {errMsg && (
          <div className="mb-4 p-3 rounded-lg bg-red-50 text-red-600 text-sm border border-red-200">{errMsg}</div>
        )}

        <label className="block mb-3">
          <span className="text-sm text-slate-600">KYC ID</span>
          <input
            type="text"
            placeholder="Enter your KYC ID"
            value={kycId}
            onChange={(e) => setKycId(e.target.value)}
            className="mt-1 w-full border border-slate-200 rounded-lg p-3 focus:ring-2 focus:ring-blue-200 focus:outline-none"
          />
        </label>

        <label className="block mb-4">
          <span className="text-sm text-slate-600">Password</span>
          <div className="mt-1 relative flex items-center">
            <input
              type={showPass ? "text" : "password"}
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border border-slate-200 rounded-lg p-3 pr-10 focus:ring-2 focus:ring-blue-200 focus:outline-none"
            />
            <button type="button" className="absolute right-3 text-slate-500 hover:text-slate-700" onClick={() => setShowPass((s) => !s)}>
              {showPass ? <RiEyeOffLine size={20} /> : <RiEyeLine size={20} />}
            </button>
          </div>
        </label>

        <button type="submit" disabled={loading}
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-lg shadow-md transition flex items-center justify-center gap-2">
          {loading ? "Authenticating..." : "Login"} <RiLoginBoxLine size={18} />
        </button>

        <p className="text-center text-xs text-slate-400 mt-4">By logging in, you agree to our Terms & Privacy Policy.</p>
      </motion.form>
    </div>
  );
}
