import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function KYCStep2() {
  const [docNumber, setDocNumber] = useState("");
  const navigate = useNavigate();

  const handleNext = () => {
    if (!docNumber) return alert("Please enter your document number");
    navigate("/kyc-step3");
  };

  return (
    <div className="h-screen flex justify-center items-center bg-gray-50">
      <div className="w-96 bg-white p-6 rounded shadow">
        <h1 className="text-2xl font-bold mb-4">KYC Step 2 â€“ Enter Document Details</h1>
        <input
          type="text"
          placeholder="Enter Document Number"
          value={docNumber}
          onChange={e => setDocNumber(e.target.value)}
          className="w-full border p-2 mb-4 rounded"
        />
        <button onClick={handleNext} className="w-full bg-blue-600 text-white p-2 rounded">Next</button>
      </div>
    </div>
  );
}
