// src/pages/kyc-step4.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowLeftLine } from "react-icons/ri";
import API from "../services/api";

const STEP1_KEY = "dk_kyc_step1";
const STEP2_KEY = "dk_kyc_step2_v2";
const STEP3_KEY = "dk_kyc_step3_uploads";

function maskNumber(num = "") {
  if (!num) return "—";
  const s = String(num);
  if (s.length <= 4) return "****" + s;
  const visible = s.slice(-4);
  const masked = "*".repeat(Math.max(4, s.length - 4));
  return masked + visible;
}

function StatusBadge({ text = "Completed" }) {
  return (
    <span className="ml-3 text-sm text-emerald-600 font-medium flex items-center gap-1">
      <svg width="14" height="14" viewBox="0 0 24 24" fill="none" className="inline-block">
        <path d="M20 6L9 17l-5-5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      </svg>
      {text}
    </span>
  );
}

export default function KYCStep4() {
  const navigate = useNavigate();
  const [personal, setPersonal] = useState({
    name: "Customer",
    dob: "",
    fatherName: "",
    address: "",
    gender: "",
    photo: null,
  });

  const [selectedDoc, setSelectedDoc] = useState({
    addrType: "",
    addrNumber: "",
    idType: "",
    idNumber: "",
  });

  const [uploads, setUploads] = useState({
    addressFileName: null,
    identityFileName: null,
    passportPhotoName: null,
  });

  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    try {
      const s1 = JSON.parse(localStorage.getItem(STEP1_KEY) || "{}");
      const s2 = JSON.parse(localStorage.getItem(STEP2_KEY) || "{}");
      const s3 = JSON.parse(localStorage.getItem(STEP3_KEY) || "{}");

      setPersonal((p) => ({
        ...p,
        name: s1.name || p.name,
        dob: s1.dob || p.dob,
        fatherName: s1.fatherName || p.fatherName,
        address: s1.address || p.address,
        gender: s1.gender || p.gender,
        photo: s1.photo || p.photo,
      }));

      setSelectedDoc((d) => ({
        addrType: s2.addrType || s2.addressType || "",
        addrNumber: s2.addrNumber || s2.addressNumber || "",
        idType: s2.idType || s2.identityType || "",
        idNumber: s2.idNumber || s2.identityNumber || "",
      }));

      setUploads((u) => ({
        addressFileName: s3?.addressFileName || s3?.addrFileName || null,
        identityFileName: s3?.identityFileName || s3?.idFileName || null,
        passportPhotoName: s1?.photoName || s3?.photoName || null,
      }));
    } catch (err) {
      // ignore parse errors
    }
  }, []);

  const goEditPersonal = () => navigate("/kyc-step1");
  const goEditDocs = () => navigate("/kyc-step2");
  const goEditUploads = () => navigate("/kyc-step3");

  const handleConfirm = async () => {
    setError("");
    setSubmitting(true);
    try {
      const payload = {
        personal,
        documents: {
          address: {
            type: selectedDoc.addrType,
            number: selectedDoc.addrNumber,
            file: uploads.addressFileName,
          },
          identity: {
            type: selectedDoc.idType,
            number: selectedDoc.idNumber,
            file: uploads.identityFileName,
          },
        },
      };

      if (API && API.post) {
        await API.post("/kyc/submit", payload);
      }
      // set local kyc completion marker
      const existing = JSON.parse(localStorage.getItem("dk_local_kyc_status") || "{}");
      existing.status = "Completed";
      existing.submittedAt = new Date().toISOString();
      localStorage.setItem("dk_local_kyc_status", JSON.stringify(existing));

      navigate("/kyc-step5");
    } catch (err) {
      const msg =
        (err && err.response && err.response.data && err.response.data.message) ||
        err.message ||
        "Submission failed — please try again.";
      setError(msg);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-gradient-to-br from-blue-50 to-white">
      <motion.div
        initial={{ opacity: 0, y: 8 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-3xl bg-white p-8 rounded-2xl shadow"
      >
        <div className="flex items-start justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">Step <span className="font-semibold">4</span> of <span className="font-semibold">5</span></div>
            <h2 className="text-2xl font-extrabold text-slate-800">Review & confirm your details</h2>
            <p className="text-sm text-slate-500 mt-1">
              Please verify the details below before submission. You can edit any item if something looks wrong.
            </p>
          </div>

          <div className="w-36 text-right">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden mb-2">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: "80%" }} />
            </div>
            <div className="text-xs text-slate-400">Progress: 80%</div>
          </div>
        </div>

        {/* Personal details (unchanged) */}
        <section className="mb-6 p-4 rounded-lg border bg-white shadow-sm">
          <div className="flex justify-between items-start">
            <div>
              <div className="flex items-center gap-2">
                <h3 className="text-lg font-semibold">Personal details</h3>
                <StatusBadge />
              </div>

              <div className="mt-4 text-sm text-slate-700">
                <div><strong>Name:</strong> {personal.name || "—"}</div>
                <div><strong>DOB:</strong> {personal.dob || "—"}</div>
                <div><strong>Father's Name:</strong> {personal.fatherName || "—"}</div>
                <div><strong>Address:</strong> {personal.address || "—"}</div>
                <div><strong>Gender:</strong> {personal.gender || "—"}</div>
              </div>
            </div>

            <div className="flex flex-col items-end gap-4">
              <button onClick={goEditPersonal} className="px-3 py-1 border rounded text-sm bg-white">✎ Edit</button>

              <div className="w-24 h-28 bg-slate-100 rounded overflow-hidden flex items-center justify-center text-xs text-slate-400">
                {personal.photo ? (
                  <img
                    src={personal.photo.startsWith("data:") ? personal.photo : `/uploads/${personal.photo}`}
                    alt="passport"
                    className="object-cover w-full h-full"
                  />
                ) : (
                  <div>No photo</div>
                )}
              </div>
            </div>
          </div>
        </section>

        {/* Address proof selected */}
        <section className="mb-4 p-4 rounded-lg border bg-white shadow-sm">
          <div className="flex justify-between items-center">
            <div>
              <h4 className="text-lg font-semibold">Address proof (selected)</h4>
              <div className="text-sm text-slate-600 mt-2">
                <div><strong>Document:</strong> {selectedDoc.addrType || "—"}</div>
                <div className="text-xs text-slate-400 mt-1">This is the address proof you selected in Step 2.</div>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <StatusBadge />
              <button onClick={goEditDocs} className="px-3 py-1 border rounded text-sm bg-white">✎ Edit</button>
            </div>
          </div>
        </section>

        {/* Identity proof selected */}
        <section className="mb-4 p-4 rounded-lg border bg-white shadow-sm">
          <div className="flex justify-between items-center">
            <div>
              <h4 className="text-lg font-semibold">Identity proof (selected)</h4>
              <div className="text-sm text-slate-600 mt-2">
                <div><strong>Document:</strong> {selectedDoc.idType || "—"}</div>
                <div className="text-xs text-slate-400 mt-1">This is the identity proof you selected in Step 2.</div>
              </div>
            </div>
            <div className="flex items-center gap-3">
              <StatusBadge />
              <button onClick={goEditDocs} className="px-3 py-1 border rounded text-sm bg-white">✎ Edit</button>
            </div>
          </div>
        </section>

        {/* Address proof uploaded */}
        <section className="mb-4 p-4 rounded-lg border bg-white shadow-sm">
          <div className="flex justify-between items-start">
            <div>
              <h4 className="text-lg font-semibold">Address proof (uploaded)</h4>
              <div className="text-sm text-slate-600 mt-2">
                {uploads.addressFileName ? (
                  <>
                    <div className="font-medium">{uploads.addressFileName}</div>
                    <div className="text-xs text-slate-400 mt-1">Uploaded in Step 3.</div>
                  </>
                ) : (
                  <div className="text-slate-500">No address proof uploaded</div>
                )}
              </div>
            </div>
            <div className="flex items-center gap-3">
              <StatusBadge text={uploads.addressFileName ? "Completed" : "Not uploaded"} />
              <button onClick={goEditUploads} className="px-3 py-1 border rounded text-sm bg-white">Change</button>
            </div>
          </div>
        </section>

        {/* Identity proof uploaded */}
        <section className="mb-6 p-4 rounded-lg border bg-white shadow-sm">
          <div className="flex justify-between items-start">
            <div>
              <h4 className="text-lg font-semibold">Identity proof (uploaded)</h4>
              <div className="text-sm text-slate-600 mt-2">
                {uploads.identityFileName ? (
                  <>
                    <div className="font-medium">{uploads.identityFileName}</div>
                    <div className="text-xs text-slate-400 mt-1">Uploaded in Step 3.</div>
                  </>
                ) : (
                  <div className="text-slate-500">No identity proof uploaded</div>
                )}
              </div>
            </div>
            <div className="flex items-center gap-3">
              <StatusBadge text={uploads.identityFileName ? "Completed" : "Not uploaded"} />
              <button onClick={goEditUploads} className="px-3 py-1 border rounded text-sm bg-white">Change</button>
            </div>
          </div>
        </section>

        {error && <div className="mb-4 text-sm text-red-600">{error}</div>}

        <div className="flex items-center gap-3">
          <button
            onClick={() => navigate("/kyc-step3")}
            className="flex items-center gap-2 px-4 py-2 rounded border bg-white"
          >
            <RiArrowLeftLine /> Back
          </button>

          <button
            onClick={handleConfirm}
            disabled={submitting}
            className="ml-auto px-6 py-3 bg-emerald-600 text-white rounded inline-flex items-center gap-2 shadow"
          >
            {submitting ? "Submitting..." : "Confirm ➜"}
          </button>
        </div>
      </motion.div>
    </div>
  );
}
