// src/pages/kyc-step4.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowLeftLine, RiCheckboxCircleFill } from "react-icons/ri";
import API from "../services/api";

function maskNumber(num) {
  if (!num) return "";
  const s = String(num);
  if (s.length <= 4) return s;
  const maskedLen = Math.max(0, s.length - 4);
  return "*".repeat(maskedLen) + s.slice(-4);
}

export default function KYCStep4() {
  const navigate = useNavigate();

  const [personal, setPersonal] = useState({
    name: "Customer",
    dob: "",
    fatherName: "",
    address: "",
    gender: "",
    photoName: null,
  });

  const [selected, setSelected] = useState({
    addrType: "Aadhaar Card",
    addrNumber: "",
    idType: "PAN Card",
    idNumber: "",
  });

  const [uploads, setUploads] = useState({
    addrFileName: null,
    idFileName: null,
  });

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    try {
      const s1 = JSON.parse(localStorage.getItem("dk_kyc_step1") || "{}");
      const s2 = JSON.parse(localStorage.getItem("dk_kyc_step2_v2") || "{}");
      const s3 = JSON.parse(localStorage.getItem("dk_kyc_step3_files") || "{}");

      setPersonal((p) => ({
        ...p,
        name: s1.name || p.name,
        dob: s1.dob || p.dob,
        fatherName: s1.fatherName || p.fatherName,
        address: s1.address || p.address,
        gender: s1.gender || p.gender,
        photoName: s1.photoName || p.photoName,
      }));

      setSelected((t) => ({
        addrType: s2.addrType || t.addrType,
        addrNumber: s2.addrNumber || t.addrNumber,
        idType: s2.idType || t.idType,
        idNumber: s2.idNumber || t.idNumber,
      }));

      setUploads((u) => ({
        addrFileName: s3.addrFileName || null,
        idFileName: s3.idFileName || null,
      }));
    } catch (e) {
      console.warn("Error reading KYC localStorage", e);
    }
  }, []); // keep empty deps intentionally

  const goEditPersonal = () => navigate("/kyc-step1");
  const goEditDocDetails = () => navigate("/kyc-step2");
  const goUpload = () => navigate("/kyc-step3");

  const handleConfirm = async (e) => {
    if (e) e.preventDefault();
    setError("");
    setLoading(true);
    try {
      await API.post("/kyc/submit", {});
      localStorage.setItem(
        "dk_local_kyc_status",
        JSON.stringify({ status: "Completed", submittedAt: new Date().toISOString() })
      );
      setLoading(false);
      navigate("/kyc-step5");
    } catch (err) {
      console.error("Error submitting KYC", err);
      setError(err?.response?.data?.message || "Failed to submit KYC. Try again.");
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-gradient-to-br from-blue-50 to-white">
      <motion.form
        onSubmit={handleConfirm}
        initial={{ opacity: 0, y: 6 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-3xl bg-white p-8 rounded-2xl shadow-lg border border-gray-100"
      >
        <div className="flex items-start justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">
              Step <span className="font-semibold">4</span> of <span className="font-semibold">5</span>
            </div>
            <h2 className="text-2xl font-extrabold text-slate-800">Review & confirm your details</h2>
            <p className="text-sm text-slate-500 mt-1">
              Please verify the details below before submission. You can edit any item if something looks wrong.
            </p>
          </div>

          <div className="w-40">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden mb-2">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: "80%" }} />
            </div>
            <div className="text-xs text-slate-400">Progress: 80%</div>
          </div>
        </div>

        {/* Personal details */}
        <section className="mb-6 p-4 border rounded-lg bg-white">
          <div className="flex justify-between items-start">
            <div className="flex items-center gap-3">
              <h3 className="text-lg font-semibold">Personal details</h3>
              <div className="text-sm text-emerald-600 flex items-center gap-1">
                <RiCheckboxCircleFill /> <span className="text-sm">Completed</span>
              </div>
            </div>

            <button type="button" onClick={goEditPersonal} className="px-3 py-1 border rounded text-sm">
              ✎ Edit
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4 text-sm">
            <div>
              <div className="text-slate-600 font-medium">Name:</div>
              <div className="mt-1 font-medium">{personal.name || "—"}</div>
            </div>

            <div>
              <div className="text-slate-600 font-medium">DOB:</div>
              <div className="mt-1">{personal.dob || "—"}</div>
            </div>

            <div className="flex flex-col items-end">
              <div className="text-slate-600 font-medium">Photo</div>
              <div className="mt-2 w-20 h-24 bg-slate-100 rounded text-xs flex items-center justify-center">
                {personal.photoName ? <div className="text-xs">{personal.photoName}</div> : <div className="text-slate-400">No photo</div>}
              </div>
            </div>

            <div>
              <div className="text-slate-600 font-medium">Father's Name:</div>
              <div className="mt-1">{personal.fatherName || "—"}</div>
            </div>

            <div>
              <div className="text-slate-600 font-medium">Address:</div>
              <div className="mt-1">{personal.address || "—"}</div>
            </div>

            <div>
              <div className="text-slate-600 font-medium">Gender:</div>
              <div className="mt-1">{personal.gender || "—"}</div>
            </div>
          </div>
        </section>

        {/* Address proof (selected) */}
        <section className="mb-4 p-4 border rounded-lg bg-white">
          <div className="flex justify-between items-center">
            <div>
              <h4 className="font-semibold">Address proof (selected)</h4>
              <div className="text-sm text-slate-500 mt-1">This is the address proof you selected in Step 2.</div>
            </div>

            <div className="flex items-center gap-3">
              <div className="text-sm text-emerald-600 flex items-center gap-1">
                <RiCheckboxCircleFill /> <span className="text-sm">Completed</span>
              </div>
              <button type="button" onClick={goEditDocDetails} className="px-3 py-1 border rounded text-sm">
                ✎ Edit
              </button>
            </div>
          </div>

          <div className="mt-3 text-sm">
            <div className="font-medium">{selected.addrType}</div>
            <div className="text-xs text-slate-400 mt-1">
              Document number: <span className="font-semibold">{selected.addrNumber ? maskNumber(selected.addrNumber) : "—"}</span>
            </div>
          </div>
        </section>

        {/* Identity proof (selected) */}
        <section className="mb-4 p-4 border rounded-lg bg-white">
          <div className="flex justify-between items-center">
            <div>
              <h4 className="font-semibold">Identity proof (selected)</h4>
              <div className="text-sm text-slate-500 mt-1">This is the identity proof you selected in Step 2.</div>
            </div>

            <div className="flex items-center gap-3">
              <div className="text-sm text-emerald-600 flex items-center gap-1">
                <RiCheckboxCircleFill /> <span className="text-sm">Completed</span>
              </div>
              <button type="button" onClick={goEditDocDetails} className="px-3 py-1 border rounded text-sm">
                ✎ Edit
              </button>
            </div>
          </div>

          <div className="mt-3 text-sm">
            <div className="font-medium">{selected.idType}</div>
            <div className="text-xs text-slate-400 mt-1">
              Document number: <span className="font-semibold">{selected.idNumber ? maskNumber(selected.idNumber) : "—"}</span>
            </div>
          </div>
        </section>

        {/* Address proof (uploaded) */}
        <section className="mb-4 p-4 border rounded-lg bg-white">
          <div className="flex justify-between items-center">
            <div>
              <h4 className="font-semibold">Address proof (uploaded)</h4>
              <div className="text-sm text-slate-500 mt-1">The file you uploaded in Step 3 will be used to verify the document.</div>
            </div>

            <div className="flex items-center gap-3">
              <div className={`text-sm ${uploads.addrFileName ? "text-emerald-600" : "text-amber-600"}`}>
                {uploads.addrFileName ? "✓ Uploaded" : "⚠ Not uploaded"}
              </div>
              <button type="button" onClick={goUpload} className="px-3 py-1 border rounded text-sm">
                Change
              </button>
            </div>
          </div>

          <div className="mt-3 text-sm text-slate-600">{uploads.addrFileName ? <div className="font-medium">{uploads.addrFileName}</div> : <div>No address proof uploaded</div>}</div>
        </section>

        {/* Identity proof (uploaded) */}
        <section className="mb-4 p-4 border rounded-lg bg-white">
          <div className="flex justify-between items-center">
            <div>
              <h4 className="font-semibold">Identity proof (uploaded)</h4>
              <div className="text-sm text-slate-500 mt-1">The file you uploaded in Step 3 will be used to verify the document.</div>
            </div>

            <div className="flex items-center gap-3">
              <div className={`text-sm ${uploads.idFileName ? "text-emerald-600" : "text-amber-600"}`}>{uploads.idFileName ? "✓ Uploaded" : "⚠ Not uploaded"}</div>
              <button type="button" onClick={goUpload} className="px-3 py-1 border rounded text-sm">
                Change
              </button>
            </div>
          </div>

          <div className="mt-3 text-sm text-slate-600">{uploads.idFileName ? <div className="font-medium">{uploads.idFileName}</div> : <div>No identity proof uploaded</div>}</div>
        </section>

        {error && <div className="text-sm text-red-600 mb-3">{error}</div>}

        <div className="flex gap-3 mt-6 items-center">
          <button type="button" onClick={() => navigate("/kyc-step3")} className="px-4 py-2 rounded border bg-white inline-flex items-center gap-2">
            <RiArrowLeftLine /> Back
          </button>

          <button type="submit" disabled={loading} className="ml-auto px-6 py-3 bg-green-600 text-white rounded inline-flex items-center gap-2">
            {loading ? "Submitting..." : "Confirm →"}
          </button>
        </div>
      </motion.form>
    </div>
  );
}
