import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";

export default function KYCStep2() {
  const [docNumber, setDocNumber] = useState("");
  const navigate = useNavigate();

  const handleNext = () => {
    if (!docNumber) return alert("Please enter your document number");
    navigate("/kyc-step3");
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gradient-to-br from-blue-50 to-white p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl border border-gray-200"
      >
        <h2 className="text-blue-700 font-bold text-xl mb-2 text-center">Step 2 of 5</h2>
        <h1 className="text-3xl font-extrabold mb-6 text-gray-800 text-center">Enter Document Details</h1>

        <input
          type="text"
          placeholder="Enter Document Number"
          value={docNumber}
          onChange={e => setDocNumber(e.target.value)}
          className="w-full border-gray-300 border rounded-xl p-3 mb-6 focus:ring-2 focus:ring-blue-500 focus:outline-none transition duration-200"
        />

        <button
          onClick={handleNext}
          className="w-full bg-blue-700 hover:bg-blue-800 text-white font-semibold py-3 rounded-xl flex justify-center items-center gap-2 shadow-lg transition duration-300"
        >
          Next <RiArrowRightLine size={22} />
        </button>
      </motion.div>
    </div>
  );
}
