// src/pages/kyc-step5.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiDownloadCloudLine, RiCheckLine, RiArrowRightLine } from "react-icons/ri";
import HDFCLogo from "../assets/hdfc.png";

/**
 * KYC Step 5 - Submission complete + Download Confirmation
 * - Reads data from localStorage (kycPersonal, kycDocument, kycPhoto, kycCompleteResponse)
 * - Shows success UI
 * - Download Confirmation opens printable HTML with logo & data and calls print()
 */

function maskAccountNo(acc) {
  if (!acc) return "Not assigned";
  const s = String(acc);
  const last4 = s.slice(-4);
  return `XXXX-XXXX-${last4}`;
}
function maskDocNumber(s) {
  if (!s) return "—";
  const st = String(s);
  if (st.length <= 4) return "****";
  return `${"*".repeat(Math.max(0, st.length - 4))}${st.slice(-4)}`;
}
function formatDateTime(isoOrMs) {
  const d = isoOrMs ? new Date(isoOrMs) : new Date();
  return d.toLocaleString();
}

export default function KYCStep5() {
  const navigate = useNavigate();
  const [personal, setPersonal] = useState(null);
  const [document, setDocument] = useState(null);
  const [photo, setPhoto] = useState(null);
  const [completeResp, setCompleteResp] = useState(null);

  useEffect(() => {
    try {
      setPersonal(JSON.parse(localStorage.getItem("kycPersonal") || "null"));
      setDocument(JSON.parse(localStorage.getItem("kycDocument") || "null"));
      setPhoto(localStorage.getItem("kycPhoto") || null);
      setCompleteResp(JSON.parse(localStorage.getItem("kycCompleteResponse") || "null"));
    } catch (e) {
      console.warn("Failed to read KYC data:", e);
    }
  }, []);

  const handleDownload = () => {
    const applicant = personal?.fullName || "Customer";
    const kycId = completeResp?.kycId ?? "N/A";
    const accountNo = completeResp?.accountNo ?? null;
    const status = completeResp?.status ?? "Submitted";
    const submittedAt = completeResp?.submittedAt ?? new Date().toISOString();
    const docType = document?.docType ?? "—";
    const docNumber = document?.docNumber ?? "—";

    // Use webpack-provided logo URL
    const logoUrl = HDFCLogo;

    const photoHtml = photo
      ? `<div style="margin-top:12px;"><img src="${photo}" alt="passport" style="width:96px;height:120px;object-fit:cover;border-radius:6px;border:1px solid #e6e6e6" /></div>`
      : "";

    // simple printable HTML (inline styles for reliability)
    const html = `
      <html>
      <head>
        <title>KYC Confirmation - ${escapeHtml(applicant)}</title>
        <meta name="viewport" content="width=device-width,initial-scale=1" />
        <style>
          body{font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Helvetica,Arial,sans-serif;color:#12202a;margin:0;padding:24px;background:#f6f9fb}
          .box{max-width:760px;margin:0 auto;background:#fff;border-radius:10px;padding:28px;border:1px solid #e8eef6}
          .header{display:flex;align-items:center;gap:16px}
          .logo{width:56px;height:56px;border-radius:8px;overflow:hidden;display:flex;align-items:center;justify-content:center;box-shadow:0 2px 6px rgba(0,0,0,0.06)}
          .title{font-size:20px;font-weight:700;color:#0b2540}
          .hero{margin-top:18px;padding:18px;background:linear-gradient(90deg,#eaf5ff,#eef8ff);border-radius:8px}
          .hero h1{color:#0b4aa2;margin:0;font-size:22px}
          .hero p{margin:6px 0 0;color:#44566b}
          .grid{display:flex;gap:18px;margin-top:20px;flex-wrap:wrap}
          .card{flex:1 1 300px;background:#fff;padding:14px;border-radius:8px;border:1px solid #f0f4f8}
          table{width:100%;border-collapse:collapse;margin-top:8px}
          td{padding:8px 6px;border-bottom:1px dashed #eef2f7}
          td.label{width:36%;font-weight:600;color:#0b3b63}
          .muted{color:#6b7a89;font-size:13px}
          .footer{margin-top:18px;color:#6b7a89;font-size:13px}
          @media print {
            body{background:white}
            .controls{display:none}
            .box{border:none;box-shadow:none}
          }
        </style>
      </head>
      <body>
        <div class="box">
          <div class="header">
            <div class="logo"><img src="${logoUrl}" alt="logo" style="height:100%;width:auto;display:block" /></div>
            <div>
              <div style="font-size:14px;color:#6b7a89">Digital KYC</div>
              <div style="font-size:18px;font-weight:800;color:#0b2540">KYC Confirmation</div>
            </div>
          </div>

          <div class="hero">
            <h1>Congratulations, ${escapeHtml(applicant)}!</h1>
            <p>Your KYC submission is received and is under verification. Keep this confirmation for your records.</p>
          </div>

          <div class="grid">
            <div class="card">
              <table>
                <tr><td class="label">Applicant</td><td>${escapeHtml(applicant)}</td></tr>
                <tr><td class="label">KYC ID</td><td>${escapeHtml(String(kycId))}</td></tr>
                <tr><td class="label">Account No</td><td>${escapeHtml(accountNo ? maskAccountNo(accountNo) : "Not assigned")}</td></tr>
                <tr><td class="label">Status</td><td>${escapeHtml(status)}</td></tr>
                <tr><td class="label">Submitted at</td><td>${escapeHtml(formatDateTime(submittedAt))}</td></tr>
                <tr><td class="label">Document</td><td>${escapeHtml(docType)} / ${escapeHtml(maskDocNumber(String(docNumber)))}</td></tr>
              </table>
            </div>

            <div class="card">
              <div style="font-weight:700;color:#0b2540">Important</div>
              <div class="muted" style="margin-top:8px">
                Please keep this confirmation for your records. The full document number was submitted securely and will not be shown on this page for privacy.
              </div>

              ${photoHtml}
            </div>
          </div>

          <div class="footer">Generated by Digital KYC System.</div>
        </div>
      </body>
      </html>
    `;

    const w = window.open("", "_blank", "noopener,noreferrer");
    if (!w) {
      alert("Popup blocked — allow popups for this site to download the confirmation.");
      return;
    }
    w.document.open();
    w.document.write(html);
    w.document.close();

    // small delay to allow assets to render, then print
    setTimeout(() => {
      w.focus();
      w.print();
    }, 350);
  };

  const handleDashboard = () => navigate("/dashboard");

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-8">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-xl p-8 text-center">
        <div className="flex items-center justify-center mb-4">
          <div className="rounded-full bg-green-50 p-4 inline-flex items-center justify-center">
            <RiCheckLine className="text-green-600" size={36} />
          </div>
        </div>

        <h2 className="text-2xl font-extrabold mb-2">KYC Submitted</h2>
        <p className="text-gray-600 mb-6">Thanks {personal?.fullName || "Customer"} — your KYC is submitted.</p>

        <div className="flex gap-4 justify-center mb-4">
          <button
            onClick={handleDownload}
            className="inline-flex items-center gap-2 px-5 py-3 bg-blue-700 text-white rounded-lg shadow hover:opacity-95"
          >
            <RiDownloadCloudLine /> Download Confirmation
          </button>

          <button
            onClick={handleDashboard}
            className="inline-flex items-center gap-2 px-5 py-3 bg-green-600 text-white rounded-lg shadow hover:opacity-95"
          >
            Go to Dashboard <RiArrowRightLine />
          </button>
        </div>

        <div className="text-sm text-gray-400">Profile {personal ? "available" : "not available"}</div>
      </div>
    </div>
  );
}

// helpers
function escapeHtml(str) {
  if (!str) return "";
  return String(str)
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#039;");
}
