// src/components/Layout.jsx
import React from "react";
import { Link, Outlet } from "react-router-dom";

export default function Layout() {
  return (
    <div className="min-h-screen flex flex-col">
      {/* ---------- Header ---------- */}
      <header className="bg-blue-700 text-white p-4 flex justify-between items-center shadow-lg">
        <h1 className="text-xl font-bold">HDFC Digital KYC</h1>

        <nav className="space-x-4">
          <Link to="/" className="hover:underline">Home</Link>
          <Link to="/dashboard" className="hover:underline">Dashboard</Link>
        </nav>
      </header>

      {/* ---------- Main Content ---------- */}
      <main className="flex-1 bg-blue-50 p-4">
        {/* IMPORTANT: This renders the routed page */}
        <Outlet />
      </main>

      {/* ---------- Footer ---------- */}
      <footer className="bg-gray-100 text-gray-600 text-center p-4">
        &copy; 2025 HDFC Bank. All rights reserved.
      </footer>
    </div>
  );
}
