import { useState, useContext } from "react";
import API, { setToken } from "../services/api";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

export default function Login() {
  const [kycId, setKycId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleLogin = async () => {
    try {
      const res = await API.post("/auth/login", { kycId, password });
      setToken(res.data.token);
      login(res.data.token, res.data.role);
      navigate("/dashboard");
    } catch (err) {
      alert(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="h-screen flex justify-center items-center bg-gray-50">
      <div className="w-96 p-6 bg-white rounded shadow-md">
        <h1 className="text-2xl font-bold mb-4">Login</h1>
        <input placeholder="KYC ID" className="w-full border p-2 mb-2 rounded" onChange={e=>setKycId(e.target.value)} />
        <input type="password" placeholder="Password" className="w-full border p-2 mb-2 rounded" onChange={e=>setPassword(e.target.value)} />
        <button onClick={handleLogin} className="w-full bg-green-600 text-white p-2 rounded mt-2">Login</button>
      </div>
    </div>
  );
}
