// src/pages/kyc-step4.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowLeftLine, RiEdit2Line } from "react-icons/ri";

/**
 * Step 4 - Review & confirm your details
 *
 * Expects previous steps to save to localStorage:
 *  - dk_kyc_step1        -> { name, dob, fatherName, address, gender, photoName }
 *  - dk_kyc_step2_v2     -> { addrType, addrNumber, idType, idNumber }
 *  - dk_kyc_step3_files  -> { addrFileName, idFileName }
 *
 * On confirm: writes dk_local_kyc_status = { status: 'Completed', submittedAt: ..., kycId? }
 * then navigates to /kyc-step5
 */

function safeParse(key) {
  try {
    return JSON.parse(localStorage.getItem(key) || "{}");
  } catch {
    return {};
  }
}

function maskNumber(num) {
  if (!num) return "—";
  const s = String(num);
  if (s.length <= 4) return s;
  const last = s.slice(-4);
  const masked = "*".repeat(Math.max(0, s.length - 4));
  // insert a few spaces (or formatting) for readability
  return masked + last;
}

export default function KYCStep4() {
  const navigate = useNavigate();

  // local state populated from localStorage
  const [personal, setPersonal] = useState({
    name: "Customer",
    dob: "",
    fatherName: "",
    address: "",
    gender: "",
    photoName: "",
  });

  const [selectedDocs, setSelectedDocs] = useState({
    addrType: "Aadhaar Card",
    addrNumber: "",
    idType: "PAN Card",
    idNumber: "",
  });

  const [uploadedFiles, setUploadedFiles] = useState({
    addrFileName: null,
    idFileName: null,
  });

  useEffect(() => {
    // read saved details (safe)
    const p = safeParse("dk_kyc_step1");
    const s = safeParse("dk_kyc_step2_v2");
    const f = safeParse("dk_kyc_step3_files");

    setPersonal((prev) => ({
      ...prev,
      name: p.name || prev.name,
      dob: p.dob || prev.dob,
      fatherName: p.fatherName || prev.fatherName,
      address: p.address || prev.address,
      gender: p.gender || prev.gender,
      photoName: p.photoName || prev.photoName,
    }));

    setSelectedDocs((prev) => ({
      ...prev,
      addrType: s.addrType || prev.addrType,
      addrNumber: s.addrNumber || prev.addrNumber,
      idType: s.idType || prev.idType,
      idNumber: s.idNumber || prev.idNumber,
    }));

    setUploadedFiles((prev) => ({
      ...prev,
      addrFileName: f.addrFileName || prev.addrFileName,
      idFileName: f.idFileName || prev.idFileName,
    }));
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const progress = 80; // step 4 of 5

  const handleEditPersonal = () => navigate("/kyc-step1");
  const handleEditDocs = () => navigate("/kyc-step2");
  const handleChangeUploads = () => navigate("/kyc-step3");
  const handleBack = () => navigate("/kyc-step3");

  const handleConfirm = async () => {
    // if you have a backend endpoint to submit final KYC, call it here.
    // For now we mark localStorage as 'Completed' and navigate to step5 (confirmation).
    const status = {
      status: "Completed",
      submittedAt: new Date().toISOString(),
      // optionally store generated KYC ID or account no if you have it
      kycId: safeParse("dk_kyc_step1").kycId || "N/A",
    };
    localStorage.setItem("dk_local_kyc_status", JSON.stringify(status));

    // navigate to final confirmation step
    navigate("/kyc-step5");
  };

  // small helpers for UI text
  const addrSelected = selectedDocs.addrType || "Aadhaar Card";
  const idSelected = selectedDocs.idType || "PAN Card";
  const addrNumMasked = selectedDocs.addrNumber ? maskNumber(selectedDocs.addrNumber) : "—";
  const idNumMasked = selectedDocs.idNumber ? maskNumber(selectedDocs.idNumber) : "—";

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-6">
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-3xl bg-white p-8 rounded-2xl shadow-2xl border border-gray-100"
      >
        <div className="flex items-start justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">Step <span className="font-semibold">4</span> of <span className="font-semibold">5</span></div>
            <h2 className="text-2xl font-extrabold text-slate-800">Review & confirm your details</h2>
            <p className="text-sm text-slate-500 mt-1">
              Please verify the details below before submission. You can edit any item if something looks wrong.
            </p>
          </div>

          <div className="w-36 text-right">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden mb-2">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: `${progress}%` }} />
            </div>
            <div className="text-xs text-slate-400">Progress: {progress}%</div>
          </div>
        </div>

        {/* Personal details */}
        <div className="mb-6 p-5 border rounded-lg bg-white shadow-sm">
          <div className="flex justify-between items-start">
            <h3 className="text-lg font-semibold">Personal details <span className="text-sm text-emerald-600 font-medium ml-2">✓ Completed</span></h3>
            <button onClick={handleEditPersonal} className="inline-flex items-center gap-2 px-3 py-2 rounded-md border bg-white shadow-sm text-sm">
              <RiEdit2Line /> Edit
            </button>
          </div>

          <div className="mt-4 grid grid-cols-1 md:grid-cols-12 gap-4 items-start">
            <div className="md:col-span-9 text-sm text-slate-700">
              <div><span className="font-semibold">Name: </span> {personal.name || "Customer"}</div>
              <div className="mt-1"><span className="font-semibold">DOB: </span> {personal.dob || "—"}</div>
              <div className="mt-1"><span className="font-semibold">Father's Name: </span> {personal.fatherName || "—"}</div>
              <div className="mt-1"><span className="font-semibold">Address: </span> {personal.address || "—"}</div>
              <div className="mt-1"><span className="font-semibold">Gender: </span> {personal.gender || "—"}</div>
            </div>

            <div className="md:col-span-3 flex items-start justify-end">
              <div className="w-24 h-28 bg-slate-100 rounded-md border flex items-center justify-center text-slate-400">
                {personal.photoName ? (
                  // if you serve uploaded photos via /uploads/<name> uncomment next line and comment placeholder
                  // <img src={`/uploads/${personal.photoName}`} alt="passport" className="w-full h-full object-cover rounded-md" />
                  <div className="text-xs text-slate-700 text-center">{personal.photoName}</div>
                ) : (
                  <div className="text-sm text-slate-400">No photo</div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Address proof (selected) */}
        <div className="mb-4 p-4 border rounded-lg bg-white shadow-sm flex justify-between items-center">
          <div>
            <h4 className="font-semibold">Address proof <span className="text-sm text-emerald-600 font-medium ml-2">✓ Completed</span></h4>
            <div className="mt-2 text-sm text-slate-600">
              <div><span className="font-medium">Document: </span> {addrSelected}</div>
              <div className="text-xs text-slate-400 mt-1">This is the address proof you selected in Step 2.</div>
            </div>
          </div>
          <div className="flex items-center gap-3">
            <button onClick={handleEditDocs} className="inline-flex items-center gap-2 px-3 py-2 rounded-md border bg-white text-sm">
              <RiEdit2Line /> Edit
            </button>
          </div>
        </div>

        {/* Identity proof (selected) */}
        <div className="mb-4 p-4 border rounded-lg bg-white shadow-sm flex justify-between items-center">
          <div>
            <h4 className="font-semibold">Identity proof <span className="text-sm text-emerald-600 font-medium ml-2">✓ Completed</span></h4>
            <div className="mt-2 text-sm text-slate-600">
              <div><span className="font-medium">Document: </span> {idSelected}</div>
              <div className="text-xs text-slate-400 mt-1">This is the identity proof you selected in Step 2.</div>
            </div>
          </div>
          <div>
            <button onClick={handleEditDocs} className="inline-flex items-center gap-2 px-3 py-2 rounded-md border bg-white text-sm">
              <RiEdit2Line /> Edit
            </button>
          </div>
        </div>

        {/* Address proof (uploaded) */}
        <div className="mb-4 p-4 border rounded-lg bg-white shadow-sm flex justify-between items-start">
          <div>
            <h4 className="font-semibold">Address proof (uploaded) <span className={`text-sm ml-2 ${uploadedFiles.addrFileName ? "text-emerald-600" : "text-amber-600"}`}>
              {uploadedFiles.addrFileName ? "✓ Uploaded" : "✕ Not uploaded"}
            </span></h4>

            <div className="mt-2 text-sm text-slate-600">
              {uploadedFiles.addrFileName ? (
                <>
                  <div className="font-medium">{uploadedFiles.addrFileName}</div>
                  <div className="text-xs text-slate-400 mt-1">The file you uploaded in Step 3 will be used to verify the document.</div>
                </>
              ) : (
                <div className="text-sm text-slate-400">No address proof uploaded</div>
              )}
            </div>
          </div>

          <div className="flex items-center gap-3">
            <button onClick={handleChangeUploads} className="px-3 py-2 rounded-md border bg-white text-sm">Change</button>
          </div>
        </div>

        {/* Identity proof (uploaded) */}
        <div className="mb-6 p-4 border rounded-lg bg-white shadow-sm flex justify-between items-start">
          <div>
            <h4 className="font-semibold">Identity proof (uploaded) <span className={`text-sm ml-2 ${uploadedFiles.idFileName ? "text-emerald-600" : "text-amber-600"}`}>
              {uploadedFiles.idFileName ? "✓ Uploaded" : "✕ Not uploaded"}
            </span></h4>

            <div className="mt-2 text-sm text-slate-600">
              {uploadedFiles.idFileName ? (
                <>
                  <div className="font-medium">{uploadedFiles.idFileName}</div>
                  <div className="text-xs text-slate-400 mt-1">The file you uploaded in Step 3 will be used to verify the document.</div>
                </>
              ) : (
                <div className="text-sm text-slate-400">No identity proof uploaded</div>
              )}
            </div>
          </div>

          <div className="flex items-center gap-3">
            <button onClick={handleChangeUploads} className="px-3 py-2 rounded-md border bg-white text-sm">Change</button>
          </div>
        </div>

        {/* Document number masked preview */}
        <div className="mb-6 text-sm text-slate-600">
          <div><span className="font-medium">Address document number: </span> {addrNumMasked}</div>
          <div className="mt-1"><span className="font-medium">Identity document number: </span> {idNumMasked}</div>
        </div>

        <div className="flex items-center gap-3">
          <button
            onClick={handleBack}
            className="flex items-center gap-2 px-4 py-2 rounded-lg border border-slate-200 text-sm bg-white"
          >
            <RiArrowLeftLine /> Back
          </button>

          <button
            onClick={handleConfirm}
            className="ml-auto inline-flex items-center gap-2 px-5 py-3 rounded-lg bg-emerald-600 text-white font-medium shadow hover:opacity-95"
          >
            Confirm & Submit
          </button>
        </div>
      </motion.div>
    </div>
  );
}
