// src/pages/kyc-step5.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiDownloadCloudLine, RiCheckLine, RiArrowRightLine } from "react-icons/ri";
import jsPDF from "jspdf";
import API, { setToken as setApiToken } from "../services/api";
import HDFCLogoUrl from "../assets/hdfc.png";

// helpers
const maskAccount = (acc) => {
  if (!acc) return "Not assigned";
  const s = String(acc).replace(/\s+/g, "");
  return s.length <= 4 ? "****" : `XXXX-XXXX-${s.slice(-4)}`;
};
const fmtDate = (d) => new Date(d).toLocaleString();

async function urlToDataUrl(url) {
  try {
    const res = await fetch(url);
    const blob = await res.blob();
    return await new Promise((resolve, reject) => {
      const fr = new FileReader();
      fr.onload = () => resolve(fr.result);
      fr.onerror = reject;
      fr.readAsDataURL(blob);
    });
  } catch (e) {
    return null;
  }
}

export default function KYCStep5() {
  const navigate = useNavigate();
  const [personal, setPersonal] = useState(null);
  const [docData, setDocData] = useState(null);
  const [submittedAt, setSubmittedAt] = useState(null);
  const [loading, setLoading] = useState(false);

  // load from localStorage (set earlier during steps)
  useEffect(() => {
    try {
      const p = JSON.parse(localStorage.getItem("kycPersonal") || "null");
      const d = JSON.parse(localStorage.getItem("kycDocument") || "null");
      const resp = JSON.parse(localStorage.getItem("kycCompleteResponse") || "null");
      setPersonal(p);
      setDocData(d);
      if (resp?.submittedAt) setSubmittedAt(resp.submittedAt);
    } catch {
      setPersonal(null);
      setDocData(null);
    }
  }, []);

  // generate a pseudo account number (if not assigned) and save Completed status
  const finalizeAndPersist = (extra = {}) => {
    const now = new Date().toISOString();

    // derive basic fields
    const fullName = (personal && (personal.fullName || personal.name)) || "Customer";
    const email = (personal && personal.email) || localStorage.getItem("userEmail") || "";
    const mobile = (personal && personal.mobile) || localStorage.getItem("userMobile") || "";
    const kycId = localStorage.getItem("userKycId") || `KYC-${Math.random().toString(36).slice(2, 10).toUpperCase()}`;
    const accountNo = localStorage.getItem("userAccountNo") || `AC${Math.floor(100000000000 + Math.random() * 900000000000)}`;

    // build confirm object
    const confirm = {
      fullName,
      email,
      mobile,
      kycId,
      accountNo,
      status: "Completed",
      submittedAt: now,
      docType: docData?.docType || (docData && docData.type) || "—",
      docNumber: docData?.docNumber || "—",
      ...extra,
    };

    // save to localStorage so dashboard picks it up
    localStorage.setItem("userName", confirm.fullName);
    localStorage.setItem("userEmail", confirm.email);
    localStorage.setItem("userMobile", confirm.mobile);
    localStorage.setItem("userKycId", confirm.kycId);
    localStorage.setItem("userAccountNo", confirm.accountNo);
    localStorage.setItem("userKycStatus", confirm.status);
    localStorage.setItem("kycCompleteResponse", JSON.stringify(confirm));
    setSubmittedAt(now);

    return confirm;
  };

  // create PDF using jsPDF and download
  const generatePdfAndDownload = async () => {
    if (loading) return;
    setLoading(true);

    // ensure we persist user as completed BEFORE download (so dashboard shows Completed)
    const confirm = finalizeAndPersist();

    try {
      // convert logo to dataURL
      const logoData = await urlToDataUrl(HDFCLogoUrl);

      const pdf = new jsPDF({ unit: "mm", format: "a4", orientation: "portrait" });
      const pageW = pdf.internal.pageSize.getWidth();
      const margin = 16;
      let y = margin;

      // logo
      if (logoData) {
        try {
          const imageProps = pdf.getImageProperties(logoData);
          const logoH = 12;
          const logoW = (imageProps.width / imageProps.height) * logoH;
          pdf.addImage(logoData, "PNG", margin, y, logoW, logoH);
        } catch (e) {
          // ignore
        }
      }

      pdf.setFontSize(18);
      pdf.setFont("helvetica", "bold");
      pdf.text("KYC Confirmation", margin + 40, y + 8);
      y += 22;

      pdf.setFontSize(11);
      pdf.setFont("helvetica", "normal");
      pdf.text(`Applicant: ${confirm.fullName}`, margin, y);
      y += 8;
      pdf.text(`KYC ID: ${confirm.kycId}`, margin, y);
      y += 8;
      pdf.text(`Account No: ${maskAccount(confirm.accountNo)}`, margin, y);
      y += 8;
      pdf.text(`Status: ${confirm.status}`, margin, y);
      y += 8;
      pdf.text(`Submitted at: ${fmtDate(confirm.submittedAt)}`, margin, y);
      y += 12;

      pdf.setFontSize(10);
      pdf.text(`Document: ${confirm.docType} (${confirm.docNumber ? confirm.docNumber.replace(/\d(?=\d{4})/g, "*") : "—"})`, margin, y);
      y += 12;

      pdf.setFontSize(9);
      pdf.setTextColor(120, 120, 120);
      pdf.text("Generated by HDFC Digital KYC System.", margin, pdf.internal.pageSize.getHeight() - 12);

      const filename = `KYC-confirmation-${(confirm.fullName || "customer").replace(/\s+/g, "_")}.pdf`;
      pdf.save(filename);
    } catch (err) {
      console.error("PDF error", err);
      alert("Could not generate PDF. See console.");
    } finally {
      setLoading(false);
    }
  };

  // robust go-to-dashboard handler: set api header and navigate
  const handleGoToDashboard = () => {
    const token = localStorage.getItem("token");
    if (token) {
      try { setApiToken(token); } catch (e) { /* ignore */ }
    }
    try {
      navigate("/dashboard", { replace: true });
      setTimeout(() => {
        if (window.location.pathname !== "/dashboard") window.location.href = "/dashboard";
      }, 120);
    } catch {
      window.location.href = "/dashboard";
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-8">
      <div className="w-full max-w-md bg-white rounded-2xl shadow-xl p-8 text-center">
        <div className="flex items-center justify-center mb-4">
          <div className="rounded-full bg-green-50 p-4 inline-flex items-center justify-center">
            <RiCheckLine className="text-green-600" size={36} />
          </div>
        </div>

        <h2 className="text-2xl font-extrabold mb-2">KYC Submitted</h2>
        <p className="text-gray-600 mb-6">Thanks {personal?.fullName || personal?.name || "Customer"} — your KYC is submitted.</p>

        <div className="flex gap-4 justify-center mb-4">
          <button
            type="button"
            onClick={generatePdfAndDownload}
            disabled={loading}
            className={`inline-flex items-center gap-2 px-5 py-3 bg-blue-700 text-white rounded-lg shadow ${loading ? "opacity-70" : ""}`}
          >
            <RiDownloadCloudLine /> {loading ? "Preparing PDF..." : "Download Confirmation"}
          </button>

          <button
            type="button"
            onClick={handleGoToDashboard}
            className="inline-flex items-center gap-2 px-5 py-3 bg-green-600 text-white rounded-lg shadow"
          >
            Go to Dashboard <RiArrowRightLine />
          </button>
        </div>

        <div className="text-sm text-gray-400">
          Profile {personal ? "available" : "not available"} — KYC status: <strong>Completed</strong>
        </div>
      </div>
    </div>
  );
}
