// src/pages/kyc-step4.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { RiArrowLeftLine, RiCheckLine } from "react-icons/ri";
import API from "../services/api"; // your axios instance

const STEP1_KEY = "dk_kyc_step1_v1";
const STEP2_KEY = "dk_kyc_step2_v2";
const STEP3_FILES_KEY = "dk_kyc_step3_files";

function maskDocumentNumber(num) {
  if (!num) return "";
  const s = String(num);
  if (s.length <= 4) return "****" + s;
  const last4 = s.slice(-4);
  return "*".repeat(Math.max(0, s.length - 4)) + last4;
}

function isImageUrl(url) {
  if (!url) return false;
  return url.startsWith("data:") || /\.(jpe?g|png|gif|webp)$/i.test(url) || url.startsWith("/uploads");
}

export default function KYCStep4() {
  const navigate = useNavigate();

  const [personal, setPersonal] = useState({
    name: "Customer",
    dob: "",
    fatherName: "",
    address: "",
    gender: "",
    email: "",
    mobile: "",
    selfieDataUrl: null,
  });

  const [doc, setDoc] = useState({
    addrType: "",
    addrNumber: "",
    idType: "",
    idNumber: "",
  });

  const [files, setFiles] = useState({
    addressFileName: null,
    identityFileName: null,
    passportPhotoName: null,
  });

  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");

  // load saved data once
  useEffect(() => {
    try {
      const s1 = JSON.parse(localStorage.getItem(STEP1_KEY) || "{}");
      const s2 = JSON.parse(localStorage.getItem(STEP2_KEY) || "{}");
      const s3 = JSON.parse(localStorage.getItem(STEP3_FILES_KEY) || "{}");

      setPersonal((p) => ({
        ...p,
        name: s1.name || p.name,
        dob: s1.dob || "",
        fatherName: s1.fatherName || "",
        address: s1.address || "",
        gender: s1.gender || "",
        email: s1.email || "",
        mobile: s1.mobile || "",
        selfieDataUrl: s1.selfieDataUrl || s3?.passportPhotoName || p.selfieDataUrl || null,
      }));

      setDoc({
        addrType: s2.addrType || s2.addressType || "",
        addrNumber: s2.addrNumber || s2.addressNumber || "",
        idType: s2.idType || s2.identityType || "",
        idNumber: s2.idNumber || s2.identityNumber || "",
      });

      setFiles({
        addressFileName: s3.addressFileName || s3.addressFileUrl || null,
        identityFileName: s3.identityFileName || s3.identityFileUrl || null,
        passportPhotoName: s3.passportPhotoName || s3.passportPhotoUrl || null,
      });
    } catch (e) {
      console.warn("Failed to parse saved KYC step data:", e);
    }
    // only once
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const handleBack = () => {
    // You can change this to go back to step3 or step2 depending on what you want to edit by default
    navigate("/kyc-step3");
  };

  const handleEditPersonal = () => navigate("/kyc-step1");
  const handleEditDocument = () => navigate("/kyc-step2");
  const handleChangeUpload = () => navigate("/kyc-step3");

  const handleConfirm = async () => {
    setError("");
    // basic validation: ensure required fields exist
    if (!personal.name) {
      setError("Please complete personal details before confirming.");
      return;
    }
    if (!doc.idNumber && !doc.addrNumber) {
      setError("Please provide document details.");
      return;
    }

    const payload = {
      personal,
      doc,
      files,
    };

    try {
      setSubmitting(true);
      // post to server - adjust route if needed
      const res = await API.post("/kyc/submit", payload);
      // expect { success:true, kycId:..., accountNo:..., status: 'Completed' } or similar
      const data = res?.data || {};
      // Save final confirmation locally if you want
      try {
        localStorage.setItem("dk_kyc_final", JSON.stringify({ ...payload, server: data }));
      } catch (e) { /* ignore */ }

      // navigate to step5 or result page
      navigate("/kyc-step5");
    } catch (err) {
      console.error(err);
      const msg = err?.response?.data?.message || err?.message || "Submission failed, try again.";
      setError(msg);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-6 bg-gradient-to-br from-blue-50 to-white">
      <motion.div
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.35 }}
        className="w-full max-w-3xl bg-white p-8 rounded-2xl shadow-lg border border-gray-100"
      >
        {/* Header + progress */}
        <div className="flex items-start justify-between mb-6">
          <div>
            <div className="text-sm text-slate-500">Step <span className="font-semibold">4</span> of <span className="font-semibold">5</span></div>
            <h2 className="text-2xl font-extrabold text-slate-800">Review & confirm your details</h2>
            <p className="mt-1 text-sm text-slate-500">Please verify the details below before submission. You can edit any item if something looks wrong.</p>
          </div>

          <div className="w-40 text-right">
            <div className="w-full bg-slate-100 h-2 rounded-full overflow-hidden mb-2">
              <div className="h-2 rounded-full bg-blue-600" style={{ width: "80%" }} />
            </div>
            <div className="text-xs text-slate-400">Progress: 80%</div>
          </div>
        </div>

        {/* Personal details box */}
        <div className="mb-6 border rounded-lg bg-white p-5 shadow-sm">
          <div className="flex justify-between items-start mb-4">
            <div className="flex items-center gap-3">
              <h3 className="text-lg font-semibold">Personal details</h3>
              <span className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> Completed</span>
            </div>

            <div>
              <button onClick={handleEditPersonal} className="px-3 py-2 rounded border bg-white text-sm">Edit</button>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="md:col-span-2">
              <div className="text-sm text-slate-700"><strong>Name:</strong> {personal.name || "—"}</div>
              <div className="text-sm text-slate-700 mt-1"><strong>DOB:</strong> {personal.dob || "—"}</div>
              <div className="text-sm text-slate-700 mt-1"><strong>Father's Name:</strong> {personal.fatherName || "—"}</div>
              <div className="text-sm text-slate-700 mt-1"><strong>Address:</strong> {personal.address || "—"}</div>
              <div className="text-sm text-slate-700 mt-1"><strong>Gender:</strong> {personal.gender || "—"}</div>
              <div className="text-sm text-slate-700 mt-1"><strong>Email:</strong> {personal.email || "—"}</div>
              <div className="text-sm text-slate-700 mt-1"><strong>Mobile:</strong> {personal.mobile || "—"}</div>
            </div>

            <div className="flex items-center justify-end">
              {personal.selfieDataUrl ? (
                <img src={personal.selfieDataUrl} alt="selfie" className="w-28 h-28 object-cover rounded-md border" />
              ) : files.passportPhotoName && isImageUrl(files.passportPhotoName) ? (
                <img src={files.passportPhotoName} alt="passport" className="w-28 h-28 object-cover rounded-md border" />
              ) : (
                <div className="w-28 h-28 bg-slate-50 rounded-md border flex items-center justify-center text-sm text-slate-400">No photo</div>
              )}
            </div>
          </div>
        </div>

        {/* Selected doc */}
        <div className="mb-4 border rounded-lg p-5 shadow-sm">
          <div className="flex justify-between items-center mb-3">
            <div className="flex items-center gap-3">
              <h4 className="font-semibold">Selected document</h4>
              <span className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> Selected</span>
            </div>
            <button onClick={handleEditDocument} className="px-3 py-2 rounded border bg-white text-sm">Edit</button>
          </div>

          <div className="text-sm text-slate-700">
            <div><strong>{doc.addrType || doc.idType || "Document"}</strong></div>
            <div className="text-xs text-slate-400 mt-1">This is the document type you chose in Step 2.</div>
          </div>
        </div>

        {/* Document number */}
        <div className="mb-4 border rounded-lg p-5 shadow-sm">
          <div className="flex justify-between items-center mb-3">
            <div className="flex items-center gap-3">
              <h4 className="font-semibold">Document number</h4>
              <span className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> Provided</span>
            </div>
            <button onClick={handleEditDocument} className="px-3 py-2 rounded border bg-white text-sm">Edit</button>
          </div>

          <div className="text-lg font-medium text-slate-800">
            {maskDocumentNumber(doc.idNumber || doc.addrNumber || "") || "—"}
          </div>
          <div className="text-xs text-slate-400 mt-2">Masked in UI for privacy. Full value submitted to verification service.</div>
        </div>

        {/* Uploaded file */}
        <div className="mb-6 border rounded-lg p-5 shadow-sm">
          <div className="flex justify-between items-center mb-3">
            <div className="flex items-center gap-3">
              <h4 className="font-semibold">Uploaded file</h4>
              <span className="text-sm text-green-600 flex items-center gap-1"><RiCheckLine /> Uploaded</span>
            </div>
            <div>
              <button onClick={handleChangeUpload} className="px-3 py-2 rounded border bg-white text-sm">Change</button>
            </div>
          </div>

          <div>
            {files.identityFileName ? (
              isImageUrl(files.identityFileName) ? (
                <div>
                  <div className="font-medium text-slate-800">Identity file</div>
                  <img src={files.identityFileName} alt="identity" className="mt-3 w-40 object-contain rounded border" />
                </div>
              ) : (
                <div>
                  <div className="font-medium text-slate-800">Uploaded file</div>
                  <div className="text-sm text-slate-600 mt-2">{files.identityFileName}</div>
                </div>
              )
            ) : (
              <div className="text-sm text-slate-500">No file uploaded</div>
            )}

            <div className="text-xs text-slate-400 mt-3">The file you uploaded in Step 3 will be used to verify the document.</div>
          </div>
        </div>

        {error && <div className="text-sm text-red-600 mb-4">{error}</div>}

        <div className="flex items-center gap-3">
          <button onClick={handleBack} className="px-4 py-2 rounded border bg-white inline-flex items-center gap-2">
            <RiArrowLeftLine /> Back
          </button>

          <button
            onClick={handleConfirm}
            disabled={submitting}
            className="ml-auto px-6 py-3 bg-green-600 text-white rounded inline-flex items-center gap-2"
          >
            {submitting ? "Submitting..." : "Confirm"}
          </button>
        </div>
      </motion.div>
    </div>
  );
}
