// src/pages/kyc-step5.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { RiCheckboxCircleLine, RiDownloadCloud2Line, RiArrowRightLine } from "react-icons/ri";
import { motion } from "framer-motion";
import API from "../services/api";

/**
 * Final KYC step: shows confirmation, lets user download printable confirmation,
 * then navigates to dashboard. Defensive and robust.
 */

export default function KYCStep5() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const res = await API.get("/auth/me"); // adapt path if your API uses /api/auth/me
        if (!mounted) return;
        setProfile(res.data || null);
      } catch (err) {
        // fail gracefully if profile isn't available
        console.warn("Could not fetch profile:", err?.response?.data || err?.message);
        setProfile(null);
      } finally {
        if (mounted) setLoading(false);
      }
    })();

    return () => {
      mounted = false;
    };
  }, []);

  // Escape HTML for printable HTML string
  function escapeHtml(str) {
    if (str === undefined || str === null) return "";
    return String(str)
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");
  }

  const downloadConfirmation = () => {
    const name = profile?.name || profile?.email || profile?.kycId || "Customer";
    const kycId = profile?.kycId || "N/A";
    const accountNo = profile?.accountNo || "Not assigned";
    const status = profile?.kycStatus || "Submitted";
    const date = new Date().toLocaleString();

    const html = `
      <!doctype html>
      <html>
      <head>
        <meta charset="utf-8"/>
        <title>KYC Confirmation</title>
        <style>
          body { font-family: Arial, Helvetica, sans-serif; padding: 28px; color:#111; background: #fff; }
          .box { max-width:760px; margin:0 auto; border:1px solid #e6e6e6; padding:24px; border-radius:8px; }
          .brand { display:flex; gap:12px; align-items:center; margin-bottom:14px; }
          .logo { width:56px; height:56px; border-radius:8px; display:flex; align-items:center; justify-content:center; background:#0b3d91; color:#fff; font-weight:700;}
          h1 { margin:0; color:#0b3d91; font-size:20px; }
          p { margin:8px 0; color:#333; }
          .meta { margin-top:18px; padding:12px; border-radius:6px; background:#f7fafc; border:1px solid #edf2f7; }
          .meta div { margin:6px 0; }
          .foot { margin-top:18px; color:#6b7280; font-size:13px; }
        </style>
      </head>
      <body>
        <div class="box">
          <div class="brand">
            <div class="logo">KYC</div>
            <div>
              <div style="font-size:12px;color:#6b7280">Digital KYC</div>
              <div style="font-weight:700">KYC Confirmation</div>
            </div>
          </div>

          <h1>Congratulations, ${escapeHtml(name)}!</h1>
          <p>Your KYC submission is received and is under verification. Keep this confirmation for your records.</p>

          <div class="meta">
            <div><strong>Applicant:</strong> ${escapeHtml(name)}</div>
            <div><strong>KYC ID:</strong> ${escapeHtml(kycId)}</div>
            <div><strong>Account No:</strong> ${escapeHtml(accountNo)}</div>
            <div><strong>Status:</strong> ${escapeHtml(status)}</div>
            <div><strong>Submitted at:</strong> ${escapeHtml(date)}</div>
          </div>

          <div class="foot">Generated by Digital KYC System.</div>
        </div>
      </body>
      </html>
    `;

    // open new window and trigger print dialog (user can Save as PDF)
    const w = window.open("", "_blank", "noopener,noreferrer");
    if (!w) {
      // fallback: download as HTML file
      const blob = new Blob([html], { type: "text/html" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = `kyc-confirmation-${kycId}.html`;
      document.body.appendChild(link);
      link.click();
      link.remove();
      return;
    }
    w.document.open();
    w.document.write(html);
    w.document.close();
    setTimeout(() => {
      try {
        w.print();
      } catch (err) {
        console.warn("Print failed:", err);
      }
    }, 600);
  };

  const finishAndGo = async () => {
    setSaving(true);
    try {
      // Optional endpoint to mark KYC complete on the backend; ignore errors
      await API.post("/kyc/complete").catch(() => {});
    } finally {
      setSaving(false);
      navigate("/dashboard", { state: { fromKyc: true } });
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-white p-4">
      <motion.div initial={{ opacity: 0, y: 8 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.35 }}
        className="w-full max-w-md bg-white p-8 rounded-2xl shadow-lg border border-gray-100 text-center"
      >
        <RiCheckboxCircleLine className="text-green-500 text-6xl mx-auto mb-4" />
        <h2 className="text-2xl font-extrabold mb-2">KYC Submitted</h2>
        <p className="text-gray-600 mb-4">{loading ? "Fetching your profile..." : `Thanks ${profile?.name || profile?.email || "Customer"} — your KYC is submitted.`}</p>

        <div className="flex items-center justify-center gap-3 mb-4">
          <button onClick={downloadConfirmation} className="px-4 py-2 rounded-lg bg-blue-700 text-white inline-flex items-center gap-2">
            <RiDownloadCloud2Line /> Download Confirmation
          </button>

          <button onClick={finishAndGo} className="px-4 py-2 rounded-lg bg-green-600 text-white inline-flex items-center gap-2" disabled={saving}>
            {saving ? "Please wait..." : "Go to Dashboard"} <RiArrowRightLine />
          </button>
        </div>

        <div className="text-sm text-slate-400">{profile ? `Signed in as ${profile.name || profile.email}` : "Profile not available"}</div>
      </motion.div>
    </div>
  );
}
