import { useEffect, useState, useContext } from "react";
import API from "../services/api";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [kyc, setKyc] = useState({});
  const { token } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/kyc/dashboard").then(res => setKyc(res.data));
  }, [token]);

  const goToStep = () => {
    const step = kyc.currentStep;
    navigate(`/kyc-step${step}`);
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-4">Dashboard</h1>
      <div className="bg-white p-4 rounded shadow">
        <p><strong>Application Number:</strong> {kyc.id}</p>
        <p><strong>Status:</strong> {kyc.status}</p>
        <p><strong>Current Step:</strong> {kyc.currentStep}</p>
        <p><strong>Failure Reason:</strong> {kyc.failureReason || "N/A"}</p>
        <button onClick={goToStep} className="mt-4 bg-blue-600 text-white px-4 py-2 rounded">
          Continue KYC
        </button>
      </div>
    </div>
  );
}
