import { useState, useContext } from "react";
import API from "../services/api";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleRegister = async () => {
    try {
      const res = await API.post("/auth/register", { email, mobile, password });
      alert(`Registration Successful!\nYour KYC ID: ${res.data.kycId}`);
      navigate("/login");
    } catch (err) {
      alert(err.response?.data?.message || "Error occurred");
    }
  };

  return (
    <div className="h-screen flex justify-center items-center bg-gray-100">
      <div className="w-96 p-6 bg-white rounded shadow-md">
        <h1 className="text-2xl font-bold mb-4">Register</h1>
        <input placeholder="Email" className="w-full border p-2 mb-2 rounded" onChange={e=>setEmail(e.target.value)} />
        <input placeholder="Mobile" className="w-full border p-2 mb-2 rounded" onChange={e=>setMobile(e.target.value)} />
        <input type="password" placeholder="Password" className="w-full border p-2 mb-2 rounded" onChange={e=>setPassword(e.target.value)} />
        <button onClick={handleRegister} className="w-full bg-blue-600 text-white p-2 rounded mt-2">Register</button>
      </div>
    </div>
  );
}
