import { useNavigate } from "react-router-dom";
import { useState } from "react";

export default function KYCStep1() {
  const [docType, setDocType] = useState("Aadhaar Card");
  const navigate = useNavigate();

  const handleNext = () => {
    alert(`Selected Document: ${docType}`);
    navigate("/kyc-step2");
  };

  return (
    <div className="h-screen flex justify-center items-center bg-gray-50">
      <div className="w-96 bg-white p-6 rounded shadow">
        <h1 className="text-2xl font-bold mb-4">KYC Step 1 â€“ Select Document</h1>
        <select className="w-full border p-2 mb-4 rounded" onChange={e=>setDocType(e.target.value)}>
          <option>Aadhaar Card</option>
          <option>PAN Card</option>
          <option>Passport</option>
          <option>Voter ID</option>
        </select>
        <button onClick={handleNext} className="w-full bg-blue-600 text-white p-2 rounded">Next</button>
      </div>
    </div>
  );
}
