// src/pages/dashboard.jsx
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import API from "../services/api";
import { useAuth } from "../context/AuthContext";
import {
  RiHomeLine,
  RiLogoutBoxRLine,
  RiSearchLine,
  RiNotification3Line,
  RiUser3Line,
  RiBankLine,
  RiMoneyDollarCircleLine,
  RiFileListLine,
  RiShieldCheckLine,
  RiArrowDownSLine,
  RiArrowUpSLine,
} from "react-icons/ri";

/**
 * Sophisticated Dashboard (Tailwind)
 * - Replace your existing src/pages/dashboard.jsx with this file.
 * - Requires API.get("/auth/me") to return user profile: { name, email, accountNo, balance, kycStatus, kycId }
 */

export default function Dashboard() {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [transactions, setTransactions] = useState([]);
  const [kpis, setKpis] = useState({
    accounts: 3,
    deposits: "₹1.2M",
    loans: "2 active",
    insurance: "3 policies",
  });

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const [{ data: p }, { data: txs }] = await Promise.all([
          API.get("/auth/me").catch(() => ({ data: null })),
          API.get("/accounts/recent-transactions").catch(() => ({ data: [] })),
        ]);
        if (!mounted) return;
        setProfile(p);
        setTransactions(txs || []);
      } catch (err) {
        console.warn("Dashboard load failed:", err);
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => (mounted = false);
  }, []);

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gray-50 text-slate-900">
      <div className="max-w-[1400px] mx-auto">
        <div className="grid grid-cols-12 gap-6 px-6 py-8">

          {/* ---------------- Sidebar ---------------- */}
          <aside className="col-span-12 lg:col-span-3">
            <div className="sticky top-6 space-y-6">
              <div className="bg-white p-4 rounded-2xl shadow-card-sm border border-gray-100">
                <div className="flex items-center gap-3">
                  <div className="h-12 w-12 rounded-lg flex items-center justify-center bg-blue-900 text-white font-bold">
                    {profile?.name ? profile.name[0].toUpperCase() : "U"}
                  </div>
                  <div>
                    <div className="text-sm text-slate-500">Welcome</div>
                    <div className="text-lg font-semibold">{profile?.name || profile?.email || "Customer"}</div>
                    <div className="text-xs text-slate-400 mt-1">{profile?.kycStatus ? `KYC: ${profile.kycStatus}` : "KYC pending"}</div>
                  </div>
                </div>

                <div className="mt-4 grid grid-cols-2 gap-2">
                  <button
                    onClick={() => navigate("/kyc-step1")}
                    className="text-xs py-2 rounded-md bg-blue-700 text-white hover:opacity-95 transition"
                  >
                    Complete KYC
                  </button>
                  <button
                    onClick={() => navigate("/result")}
                    className="text-xs py-2 rounded-md border border-slate-200 text-slate-700 hover:bg-slate-50 transition"
                  >
                    View Result
                  </button>
                </div>
              </div>

              <nav className="bg-white p-4 rounded-2xl shadow-card-sm border border-gray-100 space-y-2">
                <NavItem to="/dashboard" icon={<RiHomeLine />} label="Home" />
                <NavItem to="/accounts" icon={<RiBankLine />} label="Accounts" />
                <NavItem to="/deposits" icon={<RiMoneyDollarCircleLine />} label="Deposits" />
                <NavItem to="/loans" icon={<RiFileListLine />} label="Loans" />
                <NavItem to="/insurance" icon={<RiShieldCheckLine />} label="Insurance" />
              </nav>

              <div className="bg-white p-4 rounded-2xl shadow-card-sm border border-gray-100">
                <div className="flex items-center justify-between">
                  <div className="text-sm text-slate-500">Account No</div>
                  <div className="text-xs text-slate-400">{profile?.accountNo || "Not assigned"}</div>
                </div>
                <div className="mt-3 text-2xl font-bold">{profile?.balance ?? "—"}</div>
                <div className="mt-4 flex gap-2">
                  <button className="flex-1 py-2 bg-blue-700 text-white rounded-md">Transfer</button>
                  <button className="w-12 py-2 border border-slate-200 rounded-md text-slate-700">Stmt</button>
                </div>
              </div>
            </div>
          </aside>

          {/* ---------------- Main Content ---------------- */}
          <main className="col-span-12 lg:col-span-9 space-y-6">
            {/* Topbar */}
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4 w-full">
                <div className="relative flex-1">
                  <RiSearchLine className="absolute left-3 top-3 text-slate-400" />
                  <input
                    className="w-full pl-10 pr-4 py-3 rounded-2xl border border-gray-200 bg-white focus:ring-2 focus:ring-blue-100"
                    placeholder="Search transactions, services or help..."
                    aria-label="Search"
                  />
                </div>

                <div className="flex items-center gap-3">
                  <button className="p-2 rounded-lg bg-white shadow-sm border border-gray-100">
                    <RiNotification3Line size={18} />
                  </button>
                  <button className="p-2 rounded-lg bg-white shadow-sm border border-gray-100">
                    <RiUser3Line size={18} />
                  </button>
                  <button onClick={() => navigate("/")} title="Home" className="p-2 rounded-lg bg-white shadow-sm border border-gray-100">
                    <RiHomeLine />
                  </button>
                  <button onClick={handleLogout} title="Logout" className="p-2 rounded-lg bg-red-600 text-white hover:opacity-95">
                    <RiLogoutBoxRLine />
                  </button>
                </div>
              </div>
            </div>

            {/* Account highlight card */}
            <section className="bg-gradient-to-r from-blue-700 to-blue-800 text-white rounded-2xl p-6 shadow-lg flex items-center justify-between">
              <div>
                <div className="text-sm opacity-90">Primary Account</div>
                <div className="text-3xl font-bold mt-1">{profile?.balance ?? "₹ —"}</div>
                <div className="text-sm opacity-80 mt-1">{profile?.accountNo || "Account not assigned"}</div>

                <div className="mt-4 flex gap-3">
                  <button className="px-4 py-2 bg-white/10 border border-white/20 rounded-md">Transfer</button>
                  <button className="px-4 py-2 bg-white/10 border border-white/20 rounded-md">Statements</button>
                </div>
              </div>

              <div className="text-right">
                <div className="text-sm opacity-80">Available balance</div>
                <div className="text-3xl font-semibold">{profile?.balance ?? "—"}</div>
                <div className="mt-6 text-xs text-white/90">Secure • Instant • Paperless</div>
              </div>
            </section>

            {/* KPI tiles */}
            <section className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <KpiCard title="Accounts" value={kpis.accounts} icon={<RiBankLine />} delta="+2" />
              <KpiCard title="Deposits" value={kpis.deposits} icon={<RiMoneyDollarCircleLine />} delta="+8%" />
              <KpiCard title="Loans" value={kpis.loans} icon={<RiFileListLine />} delta="-1" negative />
              <KpiCard title="Insurance" value={kpis.insurance} icon={<RiShieldCheckLine />} delta="+1" />
            </section>

            {/* Main grid: transactions + profile/actions */}
            <div className="grid grid-cols-12 gap-6">
              <div className="col-span-12 lg:col-span-8">
                <div className="bg-white rounded-2xl p-4 shadow-card-sm border border-gray-100">
                  <div className="flex items-center justify-between mb-4">
                    <h3 className="text-lg font-semibold">Recent Transactions</h3>
                    <div className="text-sm text-slate-500">Last 7 days</div>
                  </div>

                  <div className="divide-y">
                    {transactions.length === 0 ? (
                      <div className="py-6 text-center text-slate-500">No recent transactions found</div>
                    ) : (
                      transactions.map((t, idx) => (
                        <TransactionRow key={t.id || idx} tx={t} />
                      ))
                    )}
                  </div>

                  <div className="mt-4 text-right">
                    <Link to="/transactions" className="text-sm text-blue-700 font-semibold">View all transactions →</Link>
                  </div>
                </div>
              </div>

              <aside className="col-span-12 lg:col-span-4">
                <div className="bg-white rounded-2xl p-4 shadow-card-sm border border-gray-100 space-y-4">
                  <div>
                    <div className="text-sm text-slate-500">Profile</div>
                    <div className="text-lg font-semibold mt-1">{profile?.name || profile?.email || "Customer"}</div>
                    <div className="text-xs text-slate-400 mt-1">{profile?.email || "—"}</div>
                  </div>

                  <div>
                    <div className="text-sm text-slate-500">KYC ID</div>
                    <div className="font-medium mt-1">{profile?.kycId || "—"}</div>
                  </div>

                  <div className="pt-2">
                    <button onClick={() => navigate("/kyc-step1")} className="w-full py-2 rounded-md bg-blue-700 text-white">Complete KYC</button>
                    <button onClick={() => navigate("/result")} className="w-full py-2 mt-2 rounded-md border border-slate-200">KYC Status</button>
                  </div>

                  <div className="pt-2 text-sm text-slate-500">
                    <div>Help & Support</div>
                    <div className="mt-1">Call: 1800-xxx-xxxx</div>
                  </div>
                </div>
              </aside>
            </div>

            {/* footer small note */}
            <div className="text-center text-xs text-slate-400 py-6">Need help? Visit branch or contact support — 24/7.</div>
          </main>
        </div>
      </div>
    </div>
  );
}

/* ---------- small subcomponents ---------- */

function NavItem({ to, icon, label }) {
  return (
    <Link to={to} className="flex items-center gap-3 p-3 rounded-lg hover:bg-slate-50 transition text-sm">
      <div className="text-blue-700 text-lg">{icon}</div>
      <div>{label}</div>
    </Link>
  );
}

function KpiCard({ title, value, icon, delta, negative }) {
  return (
    <div className="bg-white rounded-2xl p-4 shadow-card-sm border border-gray-100">
      <div className="flex items-center justify-between">
        <div className="text-slate-500 text-sm">{title}</div>
        <div className={`p-2 rounded-lg ${negative ? "bg-red-50 text-red-600" : "bg-green-50 text-green-600"}`}>
          {negative ? <RiArrowDownSLine /> : <RiArrowUpSLine />}
        </div>
      </div>
      <div className="mt-3 flex items-end justify-between">
        <div className="text-2xl font-bold">{value}</div>
        <div className="text-sm text-slate-400">{delta}</div>
      </div>
    </div>
  );
}

function TransactionRow({ tx }) {
  // tx: { id, title, amount, date, type }
  const isDebit = String(tx.amount).trim().startsWith("-");
  return (
    <div className="py-3 flex items-center justify-between">
      <div>
        <div className="font-medium">{tx.title || "Transaction"}</div>
        <div className="text-xs text-slate-400">{tx.date || "—"}</div>
      </div>

      <div className="text-right">
        <div className={`font-semibold ${isDebit ? "text-red-600" : "text-green-600"}`}>{tx.amount ?? "—"}</div>
        <div className="text-xs text-slate-400">{tx.type || "—"}</div>
      </div>
    </div>
  );
}
